ns8:component-version-script.script:
########################################################################
##SCRIPT TO INSTALL FILTER PACK AND MS EXCHANGE
########################################################################

#VALIDATION FUNCTIONS
Function check_error
{
	if ($error.count -gt 0)
	{ 
		error_exit $args[0] 
	}
}

Function error_exit
{
	echo $args[0]
	exit $lastexitcode
}

# For Client Access Server installation change startup mode of "Net.Tcp Port Sharing Service" from Manual to Auto 
Set-Service NetTcpPortSharing -StartupType Automatic
check_error "ERROR:WHILE SETTING THE STARTUP MODE OF Net.Tcp Port Sharing Service"

Write-Output "INSTALLING MICROSOFT FILTER PACK"
#SETTING THE LOCATION WHERE THE EXE OF THE FILTER PACK IS LOCATED
Set-Location $DOWNLOAD_LOCATION_FILTERPACK

#INSTALLING MICROSOFT FILTER PACK
.\FilterPackx64.exe /quiet /norestart
check_error "ERROR:WHILE INSTALLING FILTER PACK"
Write-Output "FILTER PACK SUCCESSFULLY INSTALLED"

#CREATING THE FOLDER TO EXTRACT MICROSOFT EXCHANGE SERVER
New-Item -path $EXTRACT_LOCATION_EXCHANGE -Itemtype directory

#SETTING THE LOCATION WHERE THE EXE OF THE MICROSOFT EXCHANGE SERVER IS LOCATED
Set-Location $EXCHANGE_DOWNLOAD_LOCATION

#EXTRACTING THE FILES FOR MICROSOFT EXCHANGE SERVER
./Exchange_Server_Trial.exe /extract:"$EXTRACT_LOCATION_EXCHANGE"
Write-Output "EXTRACTED MICROSOFT EXCHANGE SERVER"

#RUNNING THE SETUP FILE FOR MICROSOFT EXCHANGE SERVER
Set-Location $EXTRACT_LOCATION_EXCHANGE

Start-Sleep 120

#TESTING IF THE SETUP FILE EXISTS OR NOT
$PATH = Test-path $EXTRACT_LOCATION_EXCHANGE\setup.com

$wait_interval = 300
$wait_limit = 3000
$wait_c=0
do
{
	if ($PATH -eq $true ) {
		break
	}
	else
	{
		Start-Sleep $wait_interval
		$wait_c = $wait_c + $wait_interval
		if ($wait_c -ge $wait_limit)
			{
				echo "ERROR:EXTRACTION OF EXCHANGE INSTALLER FAILED"
				exit 1
			}
		Start-Sleep $wait_interval
	}

}while ($wait_c -le $wait_limit)

#KILLING MS EXTRACTION PROCESS 
taskkill /IM Exchange_Server_Trial.exe /F

$domain_account="$domain\$domain_username"
Write-Output "INSTALLING MICROSOFT EXCHANGE SERVER"
add-content c:\setup.ps1 "& `"$EXTRACT_LOCATION_EXCHANGE\setup.com`" /m:install /r:H C M"
schtasks /CREATE /TN "Install-MES" /SC MONTHLY /MO first /D SUN /RL HIGHEST /RU $domain_account /RP $domain_password /TR "powershell -noprofile -command &{C:\setup.ps1} -WindowStyle Hidden" /F
schtasks /RUN /TN "Install-MES"

$wait_c=0
do
{
	if (test-path -path "C:\ExchangeSetupLogs\*" -Include *.log) {
		$result=ls C:\ExchangeSetupLogs\* -Include *.log -Force | Get-Content | Select-String "The Microsoft Exchange Server setup operation completed successfully."
		if(!$result)
		{
			Start-Sleep $wait_interval
			if ($wait_c -ge $wait_limit)
			{
				echo "ERROR:EXCHANGE NOT INSTALLED PROPERLY"
				exit 1
			}
		}
		else
		{
			# Final wait for things to settle down
			Start-Sleep $wait_interval
			echo "EXCHANGE INSTALLATION SUCCESSFULLY" 
			break
		}
	}
	else
	{
		Start-Sleep $wait_interval
		$wait_c = $wait_c + $wait_interval
		if ($wait_c -ge $wait_limit)
			{
				echo "ERROR:EXCHANGE INSTALLATION FAILED"
				exit 1
			}
		Start-Sleep $wait_interval
	}

}while ($wait_c -le $wait_limit)

#DELETING THE TASK,  CREATED FOR INSTALLATION
schtasks /DELETE /F /TN "Install-MES"

#DELETING THE FILE CREATED FOR INSTALLATION OF EXCHANGE
remove-item c:\setup.ps1 -force

Restart-Service "Microsoft*"
Start-Sleep -Seconds 120

supported-os-details.exec-script:
$error.clear()
# Auto convert input properties to local Powershell variables

# Parsing JSON elements into Powershell object
function ParseJsonProps ($jsonFile)
{
 $jsonjsonCharCount = 0
 $newObjStart = $false
 $arrayStart = $false
 $tokenStart = $false
 $valueStart = $false
 $escapeStart = $false

 $jsonFileLen = $jsonFile.length
 $jsonObj = New-Object System.Text.StringBuilder

 ForEach($c in $jsonFile.ToCharArray()) {
    $jsonCharCount++
    $tmpStr = ""
    switch -regex ($c) {
        "{" { 
            if($newObjStart -eq $false) {
                $tmpStr = "(New-Object PSObject "
                $newObjStart = $true
            } else {
                $tmpStr = $c
            }
        }
        "}" {
            if($jsonCharCount -lt $jsonFileLen) {
                $tmpStr = $c
            } else {
                $tmpStr = ")"
            }
        }
        '"' {
            if($tokenStart -eq $false -and $valueStart -eq $false -and $arrayStart -eq $false) {
                $tmpStr = '| Add-Member -Passthru NoteProperty "'
            } else {
                if($escapeStart -eq $false) {
                    $tmpStr = $c
                } else {
                    $tmpStr = "```""
                    $escapeStart = $false
                }
            }
            $tokenStart = $true
        }
        ":" {
            if($valueStart -eq $true -or $arrayStart -eq $true) {
                $tmpStr = $c
            } else {
                $tmpStr = " "
            }
            $valueStart = $true
        }
        "," {
            if($arrayStart) {
                $tmpStr = $c
            } else {
                $valueStart = $false
                $tokenStart = $false
            }
        }	
        "\[" {
            $tmpStr = "@("
            $arrayStart = $true
        }
        "\]" {
            $tmpStr = ")"
            $arrayStart = $false
        }
        "``" {
            $tmpStr = "````"
        }
        "\\" {
            if($escapeStart -eq $false) {
                $escapeStart = $true
            } else {
                $tmpStr = $c
                $escapeStart = $false
            }
        }
        "\$" {
            if($escapeStart -eq $false) {
                $tmpStr = '`$'
            } else {
                $tmpStr = '`$'
                $escapeStart = $false
            }
        }
        "n" {
            if($escapeStart -eq $false) {
                $tmpStr = $c
            } else {
                $tmpStr = "``n"
                $escapeStart = $false
            }
        }
        "r" {
            if($escapeStart -eq $false) {
                $tmpStr = $c
            } else {
                $tmpStr = "``r"
                $escapeStart = $false
            }
        }
        "t" {
            if($escapeStart -eq $false) {
                $tmpStr = $c
            } else {
                $tmpStr = "``t"
                $escapeStart = $false
            }
        }
        "b" {
            if($escapeStart -eq $false) {
                $tmpStr = $c
            } else {
                $tmpStr = "``b"
                $escapeStart = $false
            }
        }
        default {
            if($escapeStart -eq $false) {
                $tmpStr = $c
            } else {
                $tmpStr = "\$c"
                $escapeStart = $false
            }
            #$tmpStr = $c
            #if($escapeStart -eq $true) {
            #    $escapeStart = $false
            #}
        }
    }                        
    [void] $jsonObj.Append($tmpStr)
 }

 $jsonObj | Invoke-Expression
}

$vmw_appd_json_file = Get-Content "$($Args[1])"
$vmw_appd_json_obj = ParseJsonProps $vmw_appd_json_file
$vmw_appd_json_props = $vmw_appd_json_obj.PsObject.Properties

# Assigning obj to local variable
ForEach ($vmw_appd_obj_prop in $vmw_appd_json_props)
{
 $vmw_appd_obj_prop_name = $vmw_appd_obj_prop.Name;
 #$vmw_appd_obj_prop_name_full = '$vmw_appd_json_obj' + '.' + $vmw_appd_obj_prop_name
 #$vmw_appd_obj_prop_value = Invoke-Expression $vmw_appd_obj_prop_name_full
 Set-Variable -Name $vmw_appd_obj_prop_name -value $vmw_appd_json_obj."$vmw_appd_obj_prop_name"
}


# Execute user's Powershell script
$error.clear()
$LASTEXITCODE = 0
. "$($Args[0])" 2>&1 | out-file -encoding Default "$($Args[3])"

if ($LASTEXITCODE -ne 0) {
 "`nABORT. LASTEXITCODE is non-zero.`n" | out-file -encoding Default -append $($Args[3])
 exit $LASTEXITCODE
}

if ($error.count -gt 0) {
 "`nABORT. Encountered error in Powershell.`n" | out-file -encoding Default -append $($Args[3])
 exit $error.count
 }

# Export Powershell variables to output properties file
ForEach ($vmw_appd_obj_prop in $vmw_appd_json_props)
{
 $vmw_appd_obj_prop_name = $vmw_appd_obj_prop.Name;
 $vmw_appd_obj_prop_type = (($vmw_appd_json_obj.($vmw_appd_obj_prop_name)).GetType()).Name

 # Export only String AppD's properties; Output Array properties is not currently supported.
 if ($vmw_appd_obj_prop_type.Contains("String")) {
      $vmw_appd_obj_prop_name_expanded = '$' + $vmw_appd_obj_prop_name

      # The ExpandString takes care of three cases. 
      # Case-I when the string is set via input properties
      # Case-II when the string is set via input properties and overridden in script (install/configure..)
      # Case-III lastly when it is only set via script (install/configure..) and left empty in input props
      #
      # Also note that powershell as is does not care if this variable was a computed 
      # or a string variable that filter happens at a different layer.
      #
      $expandedUserVariable = $ExecutionContext.InvokeCommand.ExpandString($vmw_appd_obj_prop_name_expanded)

     ($vmw_appd_obj_prop_name + '=' + $expandedUserVariable) | Add-Content $($Args[2]) 
 }
}

$error.clear()
