task.content:
#########################################################################################################
##SCRIPT FOR DOWNLOADING PRE-REQUISITE, FILTER PACK AND EXCHANGE INSTALLER
##INSTALLING PRE-REQUISITE
##NOTE:
##    CHECKS MADE ON THE INSTALLER FILE SIZE, FOR FILTER PACK AND EXCHANGE
##    NEED RESTART AFTER PRE-REQUISITE INSTALLATION, BUT IN THE SCRIPT RESTARTING THE SERVICES
#########################################################################################################

#INTERNAL PARAMETERS
$DOWNLOAD_URL_FILTERPACK="$download_url_filterpack"
$DOWNLOAD_LOCATION_FILTERPACK="$download_location_filterpack"
$DOMAIN_CONTROLLER_IP="$domain_controller_ip"
$DOMAIN="$domain"
$DOMAIN_USERNAME="domain_username"
$DOMAIN_PASSWORD="domain_password"
$ORGANIZATION_NAME="organization_name"
$DOWNLOAD_URL_EXCHANGE="$download_url_exchange"
$EXCHANGE_DOWNLOAD_LOCATION="$exchange_download_location"
$EXTRACT_LOCATION_EXCHANGE="$extract_location_exchange"

#4065080 IS THE TOTAL SIZE OF MICROSOFT FILTER PACK, IN-CASE OF SIZE CHANGE CHANGE THE BELOW LINE
$FILE_SIZE_FILTERPACK = "4065080"

#547526952 IS THE TOTAL SIZE OF EXCHANGE SERVER, IN-CASE OF SIZE CHANGE CHANGE THE BELOW LINE
$FILE_SIZE_EXCHANGE = "547526952"

#VALIDATION FUNCTIONS
Function check_error
{
	if ($error.count -gt 0)
	{ 
		error_exit $args[0] 
	}
}

Function error_exit
{
	echo $args[0]
	exit $lastexitcode
}

if (($DOWNLOAD_URL_FILTERPACK -eq ''))
{
	error_exit "ERROR:DOWNLOAD URL FOR FILTERPACK NOT SET."	
}
if (($DOWNLOAD_LOCATION_FILTERPACK -eq ''))
{
	error_exit "ERROR:DOWNLOAD LOACTION FOR FILTER PACK NOT SET."	
}
if (($DOMAIN_CONTROLLER_IP -eq ''))
{
	error_exit "ERROR:DOMAIN CONMTROLLER IP NOT SET."	
}
if (($DOMAIN -eq ''))
{
	error_exit "ERROR:DOMAIN NOT SET."	
}
if (($DOMAIN_USERNAME -eq ''))
{
	error_exit "ERROR:DOMAIN USERNAME NOT SET."	
}
if (($DOMAIN_PASSWORD -eq ''))
{
	error_exit "ERROR:DOMAIN PASSWORD NOT SET."	
}
if (($ORGANIZATION_NAME -eq ''))
{
	error_exit "ERROR:ORGANIZATION NAME NOT SET."	
}
if (($EXCHANGE_DOWNLOAD_LOCATION -eq ''))
{
	error_exit "ERROR:DOWNLOAD LOCATION FOR EXCHANGE NOT SET."	
}
if (($EXTRACT_LOCATION_EXCHANGE -eq ''))
{
	error_exit "ERROR:EXTRACT LOCATION FOR EXCHANGE NOT SET."	
}
if (($DOWNLOAD_URL_EXCHANGE -eq ''))
{
	error_exit "ERROR:DOWNLOAD URL FOR EXCHANGE SERVER NOT SET."	
}
if (($EXCHANGE_DOWNLOAD_LOCATION -eq ''))
{
	error_exit "ERROR:DOWNLOAD LOCATION FOR EXCHANGE SERVER NOT SET."	
}

#SETTING THE CLIENT FOR DOWNLOADING THE EXCHANGE SERVER
$wc=new-object system.net.webclient

#DOWNLOADING THE EXCHANGE SERVER
$wc.downloadfile($DOWNLOAD_URL_EXCHANGE,"$EXCHANGE_DOWNLOAD_LOCATION\Exchange_Server_Trial.exe")

#GETTING THE SIZE OF THE FILE
$DOWNLOADED_EXCHANGE_SERVER=(Get-Item $EXCHANGE_DOWNLOAD_LOCATION\Exchange_Server_Trial.exe).Length;

$wait_interval = 300
$wait_limit = 3000
$wait_c=0
do
{
	if ($DOWNLOADED_EXCHANGE_SERVER -match $FILE_SIZE_EXCHANGE) {
		Write-Output "EXCHANGE DOWNLOAD COMPLETE!!"
                break
	}
	else
	{
		Start-Sleep $wait_interval
		$wait_c = $wait_c + $wait_interval
		if ($wait_c -ge $wait_limit)
			{
				echo "ERROR:EXCHANGE DOWNLOAD FAILED"
				exit 1
			}
		Start-Sleep $wait_interval
	}

}while ($wait_c -le $wait_limit)


#INSTALLING THE PREREQUISITES FOR CLIENT ACCESS,MAILBOX AND HUB TRANSPORT
import-module servermanager
Add-WindowsFeature NET-Framework,RSAT-ADDS,Web-Server,Web-Basic-Auth,Web-Windows-Auth,Web-Metabase,Web-Net-Ext,Web-Lgcy-Mgmt-Console,WAS-Process-Model,RSAT-Web-Server,Web-ISAPI-Ext,Web-Digest-Auth,Web-Dyn-Compression,NET-HTTP-Activation,RPC-Over-HTTP-Proxy
Write-Output "PREREQUISITES INSTALLED"


Write-Output "DOWNLOADING MICROSOFT FILTER PACK"

#SETTING THE CLIENT FOR DOWNLOADING THE MICROSOFT FILTER PACK 
$clnt = new-object System.Net.WebClient

#DOWNLOADING THE FILTER PACK
$clnt.DownloadFile($DOWNLOAD_URL_FILTERPACK,"$DOWNLOAD_LOCATION_FILTERPACK\FilterPackx64.exe")

#GETTING THE SIZE OF FILTER PACK
$DOWNLOADED_FILTER_PACK=(Get-Item $DOWNLOAD_LOCATION_FILTERPACK\FilterPackx64.exe).Length; 

$wait_c=0
do
{
	if ($DOWNLOADED_FILTER_PACK -match $FILE_SIZE_FILTERPACK) {
		Write-Output "FILTER PACK DOWNLOAD COMPLETE!!"
                break
	}
	else
	{
		Start-Sleep $wait_interval
		$wait_c = $wait_c + $wait_interval
		if ($wait_c -ge $wait_limit)
			{
				echo "ERROR:WHILE DOWNLOADING THE FILTERPACK INSTALLER"
				exit 1
			}
		Start-Sleep $wait_interval
	}

}while ($wait_c -le $wait_limit)

Restart-Service "Microsoft*"
