ns9:component-version-script.script:
#!/bin/sh # vFabric ApplicationDirector START script for vFabric tc Server
# From ApplicationDirector - Import and source global configuration . $global_conf
# This script simply starts the tc Server instance created in the tcserver-config.sh sample script.
set -e
export PATH=$PATH:/usr/sbin:/sbin:/usr/bin:/bin export HOME=/root export tcserver_home=${tcserver_home:="/opt/vmware/vfabric-tc-server-standard"}
export JAVA_HOME=${java_home:="/usr"}
if [ -f ${tcserver_home}/${instance_name}/bin/tcruntime-ctl.sh ]; then $service_start IS_RUNNING=`$instance_dir/bin/tcruntime-ctl.sh status | grep Status | awk -F: '{ print $2 }'` if [[ "${IS_RUNNING}" == *"NOT RUNNING"* ]]; then echo "ERROR: ${tcserver_home}/${instance_name} is NOT RUNNING." echo "Please check the logs in ${tcserver_home}/${instance_name}/logs for more information" exit 1 else echo "COMPLETED: The status of your new tc Server instance is: ${IS_RUNNING}" fi fi

#!/bin/bash # vFabric ApplicationDirector Sample START script for vFabric sqlfire
# This example uses the values posted below as defaults. To change any of these # values, add the Property Name as shown below as individual properties in your # service definition in the ApplicationDirector Catalog. The value specified after # the Property name is the Type to use for the property (i.e. String, Content, Array etc) # There are two types of properties for this script: Required and Optional. Both are # listed below. # # REQUIRED PROPERTIES: # These are the properties you must add in order for this sample script to work. The property # is added when you create your service definition in the ApplicationDirector Catalog. # Property Description: Property Value settable in blueprint [type]: # -------------------------------------------------------------------------------------------- # Location of global configuration data global_conf [Content] # value: https://${darwin.server.ip}:8443/darwin/conf/darwin_global.conf # # Version of vFabric Suite release VERSION [String] # value: latest (to get most up to date version), 5.1, 5.2, etc # # If VERSION property is set to anything other then 'latest' then you need to set # REPO_RPM property. # # File name of repository RPM for given version REPO_RPM [String] # value: example = vfabric-5.1-repo-5.1-1.noarch.rpm for 5.1 repo # # OPTIONAL PROPERTIES: # Property Description: Property Name settable in blueprint: # -------------------------------------------------------------------------------------------- # which java to use JAVA_HOME [String]
# import global configuration data from ApplicationDirector . $global_conf
set -e
export PATH=$PATH:/usr/sbin:/sbin:/usr/bin:/bin export VMWARE_HOME=/opt/vmware export HOME=/root export SQLFIRE_PACKAGE=vfabric-sqlfire export SQLFIRE_VERSION=${GEMFIRE_VERSION:="103"} export SQLFIRE_HOME=${SQLFIRE_HOME:="$VMWARE_HOME/$SQLFIRE_PACKAGE/vFabric_SQLFire_$SQLFIRE_VERSION"}
# Any of the following may be set as Properties in your service definition, and if enabled, may be overwritten # in your application blueprint. export JAVA_HOME=${java_home:="/usr"} export service_start=${service_start:="${SQLFIRE_HOME}/bin/sqlf server start"} export service_stop=${service_stop:="${SQLFIRE_HOME}/bin/sqlf server stop"} export service_stop_all=${service_stop_all:="${SQLFIRE_HOME}/bin/sqlf shut-down-all"}
echo "service_start:$service_start" echo "service_stop:$service_stop" echo "service_stop_all:$service_stop_all"
if [ -f ${SQLFIRE_HOME}/bin/sqlf ]; then echo "Creating Directories For New SQLFire Cluster" cd ${SQLFIRE_HOME} for ((i=0; i<$number_of_servers; i++)) do count=$(($i + 1)) echo "count:$count" directory_name="appd_server" directory_name=$directory_name$count echo "directory_name:$directory_name" echo "Starting SQLFire Server$count" echo "locator client port:$locator_client_port" echo "locator node ip:$locator_ip" my_client_port=$(($client_port + $i)) echo "my_client_port:$client_port" #su sqlfire -c 'rm -rf $directory_name' #su sqlfire -c 'mkdir $directory_name' rm -rf $directory_name mkdir $directory_name sqlf server start -dir=${SQLFIRE_HOME}/$directory_name -locators=${locator_ip}[${peer_discovery_port}] -client-bind-address=${client_bind_address} -client-port=$my_client_port -initial-heap=$initial_heap -max-heap=$max_heap -J-Dsqlfire.prefer-netserver-ipaddress=true #status=`su sqlfire -c 'sqlf server status -dir=${SQLFIRE_HOME}/$directory_name | grep running'` status=`sqlf server status -dir=${SQLFIRE_HOME}/$directory_name | grep running` if [ -n "$status" ]; then echo "SQLFire Server$count Started" else echo "SQLFire Server$count Failed to start" exit 1 fi done # execute user-provided sql file against sqlfire # only run schema against the very first sqlfire server node
echo "node_index:$node_index" echo "schemafile:$schema_file" echo "dataload_file:$dataload_file" schema=`basename ${schema_file}` data=`basename ${dataload_file}`
export username=${username} export password=${password}
if [ "$node_index" == "0" ]; then echo "first sqlfire server node detected" if [ -f ${schema_file} -a ${schema##*.} == "sql" ]; then echo "Running schema file" echo "Loading schema ${schema_file} on ${locator_ip}" if [ ! $username == "" ]; then echo "authentication used" echo "username:$username" echo "password:$password" ${SQLFIRE_HOME}/bin/sqlf > sqlf-schema.log << EOF connect client '${locator_ip}:${locator_client_port};user=$username;password=$password'; run '${schema_file}'; exit; EOF else echo "no authentication used" ${SQLFIRE_HOME}/bin/sqlf > sqlf-schema.log << EOF connect client '${locator_ip}:${locator_client_port}'; run '${schema_file}'; exit; EOF fi fi if [ -f ${dataload_file} -a ${data##*.} == "sql" ]; then echo "Running dataload file" echo "Loading schema ${dataload_file} on ${locator_ip}" if [ ! $username == "" ]; then echo "authentication used" echo "username:$username" echo "password:$password" ${SQLFIRE_HOME}/bin/sqlf > sqlf-dataload.log << EOF connect client '${locator_ip}:${locator_client_port};user=$username;password=$password'; run '${dataload_file}'; exit; EOF else echo "no authentication required" ${SQLFIRE_HOME}/bin/sqlf > sqlf-dataload.log << EOF connect client '${locator_ip}:${locator_client_port}'; run '${dataload_file}'; exit; EOF fi fi else echo "no user-provided sql executed. non first sqlfire server node detected" fi else echo "ERROR! SQLFire executable not found in ${SQLFIRE_HOME}; Exiting" exit fi

#!/bin/bash # vFabric ApplicationDirector Sample START script for vFabric sqlfire
# This example uses the values posted below as defaults. To change any of these # values, add the Property Name as shown below as individual properties in your # service definition in the ApplicationDirector Catalog. The value specified after # the Property name is the Type to use for the property (i.e. String, Content, Array etc) # There are two types of properties for this script: Required and Optional. Both are # listed below. # # REQUIRED PROPERTIES: # These are the properties you must add in order for this sample script to work. The property # is added when you create your service definition in the ApplicationDirector Catalog. # Property Description: Property Value settable in blueprint [type]: # -------------------------------------------------------------------------------------------- # Location of global configuration data global_conf [Content] # value: https://${darwin.server.ip}:8443/darwin/conf/darwin_global.conf # # Version of vFabric Suite release VERSION [String] # value: latest (to get most up to date version), 5.1, 5.2, etc # # If VERSION property is set to anything other then 'latest' then you need to set # REPO_RPM property. # # File name of repository RPM for given version REPO_RPM [String] # value: example = vfabric-5.1-repo-5.1-1.noarch.rpm for 5.1 repo # # OPTIONAL PROPERTIES: # Property Description: Property Name settable in blueprint: # -------------------------------------------------------------------------------------------- # which java to use JAVA_HOME [String]
# import global configuration data from ApplicationDirector . $global_conf
set -e
export PATH=$PATH:/usr/sbin:/sbin:/usr/bin:/bin export VMWARE_HOME=/opt/vmware export HOME=/root export SQLFIRE_PACKAGE=vfabric-sqlfire export SQLFIRE_VERSION=${GEMFIRE_VERSION:="103"} export SQLFIRE_HOME=${SQLFIRE_HOME:="$VMWARE_HOME/$SQLFIRE_PACKAGE/vFabric_SQLFire_$SQLFIRE_VERSION"}
# Any of the following may be set as Properties in your service definition, and if enabled, may be overwritten # in your application blueprint. export JAVA_HOME=${java_home:="/usr"}
export service_start=${service_start:="${SQLFIRE_HOME}/bin/sqlf locator start"} export service_stop=${service_stop:="${SQLFIRE_HOME}/bin/sqlf locator stop"}
echo "service_start script:$service_start" echo "service_stop script:$service_stop"
if [ -f ${SQLFIRE_HOME}/bin/sqlf ]; then echo "Creating Directories For New SQLFire Cluster Locator" cd ${SQLFIRE_HOME} su sqlfire -c 'rm -rf appd_locator' su sqlfire -c 'mkdir appd_locator' echo "Starting SQLFire Locator" echo "client port ${locator_client_port}" echo "locator node ip ${node_ip}" export node_ip=$node_ip export locator_client_port=$locator_client_port cd ${SQLFIRE_HOME}/bin su sqlfire -c 'sqlf locator start -dir=${SQLFIRE_HOME}/appd_locator -peer-discovery-address=${locator_ip} -peer-discovery-port=${peer_discovery_port} -client-bind-address=${locator_ip} -client-port=${locator_client_port} -J-Dsqlfire.prefer-netserver-ipaddress=true' ##### sqlf locator start -dir=appd_locator -peer-discovery-address=10.150.108.21 -peer-discovery-port=10101 -client-bind-address=10.150.108.21 -client-port=1527 -J-Dsqlfire.prefer-netserver-ipaddress=true status=`su sqlfire -c 'sqlf locator status -dir=${SQLFIRE_HOME}/appd_locator | grep running'` if [ -n "$status" ]; then echo "SQLFire Locator Started" else echo "SQLFire Locator Failed to start" exit 1 fi #cd ${SQLFIRE_HOME}/quickstart #echo "Loading Data Into Tables" #su sqlfire -c 'sqlf run -file=ToursDB_schema.sql' #su sqlfire -c 'sqlf run -file=loadTables.sql' else echo "ERROR! SQLFire executable not found in ${SQLFIRE_HOME}; Exiting" exit fi

