ns9:component-version-script.script:
$MainNode=[System.Net.Dns]::GetHostName()
if($nodes_hostname[0] -eq $MainNode)
{	
	schtasks /DELETE /F /TN "Install-SQL"
}
else
{
	schtasks /DELETE /F /TN "Add-Node"
}

netsh advfirewall firewall add rule name='SQL Server Browser (UDP-In 1434)' dir=in program="C:\Program Files (x86)\Microsoft SQL Server\90\Shared\sqlbrowser.exe" action=allow protocol=UDP
netsh advfirewall firewall add rule name='SQL Server (TCP-In)' program='C:\Program Files\Microsoft SQL Server\MSSQL11.$instanceName\MSSQL\Binn\sqlservr.exe' dir=in action=allow protocol=TCP
netsh advfirewall firewall set rule name="Network Discovery (NB-Name-In)" new enable=yes
set-itemproperty -path "HKLM:\SOFTWARE\Microsoft\Microsoft SQL Server\MSSQL11.$instanceName\MSSQLServer\SuperSocketNetLib\Tcp" -name enabled -value 1
set-service -name SQLBrowser -StartupType automatic

supported-os-details.exec-script:
$error.clear()
