      #
      $expandedUserVariable = $ExecutionContext.InvokeCommand.ExpandString($vmw_appd_obj_prop_name_expanded)

     ($vmw_appd_obj_prop_name + '=' + $expandedUserVariable) | Add-Content $($Args[2]) 
 }
}

$error.clear()
# Auto convert input properties to local Powershell variables

# Parsing JSON elements into Powershell object
function ParseJsonProps ($jsonFile)
{
 $jsonjsonCharCount = 0
 $newObjStart = $false
 $arrayStart = $false
 $tokenStart = $false
 $valueStart = $false
 $escapeStart = $false

 $jsonFileLen = $jsonFile.length
 $jsonObj = New-Object System.Text.StringBuilder

 ForEach($c in $jsonFile.ToCharArray()) {
    $jsonCharCount++
    $tmpStr = ""
    switch -regex ($c) {
        "{" { 
            if($newObjStart -eq $false) {
                $tmpStr = "(New-Object PSObject "
                $newObjStart = $true
            } else {
                $tmpStr = $c
            }
        }
        "}" {
            if($jsonCharCount -lt $jsonFileLen) {
                $tmpStr = $c
            } else {
                $tmpStr = ")"
            }
        }
        '"' {
            if($tokenStart -eq $false -and $valueStart -eq $false -and $arrayStart -eq $false) {
                $tmpStr = '| Add-Member -Passthru NoteProperty "'
            } else {
                if($escapeStart -eq $false) {
                    $tmpStr = $c
                } else {
                    $tmpStr = "```""
                    $escapeStart = $false
                }
            }
            $tokenStart = $true
        }
        ":" {
            if($valueStart -eq $true -or $arrayStart -eq $true) {
                $tmpStr = $c
            } else {
                $tmpStr = " "
            }
            $valueStart = $true
        }
        "," {
            if($arrayStart) {
                $tmpStr = $c
            } else {
                $valueStart = $false
                $tokenStart = $false
            }
        }	
        "\[" {
            $tmpStr = "@("
            $arrayStart = $true
        }
        "\]" {
            $tmpStr = ")"
            $arrayStart = $false
        }
        "``" {
            $tmpStr = "````"
        }
        "\\" {
            if($escapeStart -eq $false) {
                $escapeStart = $true
            } else {
                $tmpStr = $c
                $escapeStart = $false
            }
        }
        "\$" {
            if($escapeStart -eq $false) {
                $tmpStr = '`$'
            } else {
                $tmpStr = '`$'
                $escapeStart = $false
            }
        }
        "n" {
            if($escapeStart -eq $false) {
                $tmpStr = $c
            } else {
                $tmpStr = "``n"
                $escapeStart = $false
            }
        }
        "r" {
            if($escapeStart -eq $false) {
                $tmpStr = $c
            } else {
                $tmpStr = "``r"
                $escapeStart = $false
            }
        }
        "t" {
            if($escapeStart -eq $false) {
                $tmpStr = $c
            } else {
                $tmpStr = "``t"
                $escapeStart = $false
            }
        }
        "b" {
            if($escapeStart -eq $false) {
                $tmpStr = $c
            } else {
                $tmpStr = "``b"
                $escapeStart = $false
            }
        }
        default {
            if($escapeStart -eq $false) {
                $tmpStr = $c
            } else {
                $tmpStr = "\$c"
                $escapeStart = $false
            }
            #$tmpStr = $c
            #if($escapeStart -eq $true) {
            #    $escapeStart = $false
            #}
        }
    }                        
    [void] $jsonObj.Append($tmpStr)
 }

 $jsonObj | Invoke-Expression
}

$vmw_appd_json_file = Get-Content "$($Args[1])"
$vmw_appd_json_obj = ParseJsonProps $vmw_appd_json_file
$vmw_appd_json_props = $vmw_appd_json_obj.PsObject.Properties

# Assigning obj to local variable
ForEach ($vmw_appd_obj_prop in $vmw_appd_json_props)
{
 $vmw_appd_obj_prop_name = $vmw_appd_obj_prop.Name;
 #$vmw_appd_obj_prop_name_full = '$vmw_appd_json_obj' + '.' + $vmw_appd_obj_prop_name
 #$vmw_appd_obj_prop_value = Invoke-Expression $vmw_appd_obj_prop_name_full
 Set-Variable -Name $vmw_appd_obj_prop_name -value $vmw_appd_json_obj."$vmw_appd_obj_prop_name"
}


# Execute user's Powershell script
$error.clear()
$LASTEXITCODE = 0
. "$($Args[0])" 2>&1 | out-file -encoding Default "$($Args[3])"

if ($LASTEXITCODE -ne 0) {
 "`nABORT. LASTEXITCODE is non-zero.`n" | out-file -encoding Default -append $($Args[3])
 exit $LASTEXITCODE
}

if ($error.count -gt 0) {
 "`nABORT. Encountered error in Powershell.`n" | out-file -encoding Default -append $($Args[3])
 exit $error.count
 }

# Export Powershell variables to output properties file
ForEach ($vmw_appd_obj_prop in $vmw_appd_json_props)
{
 $vmw_appd_obj_prop_name = $vmw_appd_obj_prop.Name;
 $vmw_appd_obj_prop_type = (($vmw_appd_json_obj.($vmw_appd_obj_prop_name)).GetType()).Name

 # Export only String AppD's properties; Output Array properties is not currently supported.
 if ($vmw_appd_obj_prop_type.Contains("String")) {
      $vmw_appd_obj_prop_name_expanded = '$' + $vmw_appd_obj_prop_name

      # The ExpandString takes care of three cases. 
      # Case-I when the string is set via input properties
      # Case-II when the string is set via input properties and overridden in script (install/configure..)
      # Case-III lastly when it is only set via script (install/configure..) and left empty in input props
      #
      # Also note that powershell as is does not care if this variable was a computed 
      # or a string variable that filter happens at a different layer.
      #
      $expandedUserVariable = $ExecutionContext.InvokeCommand.ExpandString($vmw_appd_obj_prop_name_expanded)

     ($vmw_appd_obj_prop_name + '=' + $expandedUserVariable) | Add-Content $($Args[2]) 
 }
}

task.content:
 ######################################################################################################################
# Purpose: 1. This script creates an iSCSI target.                                                                   #
#          2. This script initializes(CONNECTS) the iSCSI initiator with the created iSCSI Target                    #
# Note: 1. This script assumes an iSCSI server is already present with at least three virtual disks.                 #
#          The virtual disks have already been created on the iSCSI server in the location "C:\disks"                #
#          DISK 1 WILL BE USED FOR FAILOVER CLUSTER DISK                                                             #
#	       DISK 2 WILL BE USED FOR MSDTC SERVICE CREATED ON THE CLUSTER                                              #
#	       DISK 3 WILL USED FOR THE SQL FAILOVER CLUSTER DISK                                                        #
#       2. This Script assumes the first node is responsible for creating iSCSI target on the iSCSI server           #
######################################################################################################################

# VALIDATION FUNCTIONS 
Function check_error
{
	if ($error.count -gt 0)
	{ 
		error_exit $args[0] 
	}
}

Function error_exit
{
	echo $args[0]
	exit 1
}

Function valid_string
{ 
	$STR = $args[0]
    $ValidSTR = $true
    $ValidSTR = ($STR -match "^[A-Za-z]{1,}[A-Za-z0-9_-]{1,}$")
    if(-not $ValidSTR){return $false}
	return $ValidSTR
}

Function valid_password
{ 
	$STR = $args[0]
    $ValidSTR = $true
    $ValidSTR = ($STR -match "^[A-Za-z]{1,}[0-9_@$%^#+=]{0,}[A-Za-z0-9]{0,}$")
    if(-not $ValidSTR){return $false}
	return $ValidSTR
}

Function valid_ip
{
	$IP = $args[0]
    $ValidIP = $true

    <#Test format and leading zero (technically not necessary to test for
    leading zero as this function strips the leading zero when converting to [int]#>
    $ValidIP = ($IP -match "^[1-9]\d{0,2}\.\d{1,3}\.\d{1,3}\.\d{1,3}$") 
    if(-not $ValidIP){return $false}

    $myip = $IP -split "\." #Each octet is element of an array
    $ValidIP = ([int]$myip[0] -gt 0 -and [int]$myip[0] -le 255) #First octet is between 1-255
    if(-not $ValidIP){return $false}

    for($i = 1;$i -le 3;$i++){
        $ValidIP = ([int]$myip[$i] -ge 0 -and [int]$myip[$i] -le 255) #Remain octets between 0-255
        if(-not $ValidIP){return $false}
    }
    return $ValidIP #$ValidIP will always be true at this point.
}
# PARAMETER VALIDATION
echo "PARAMETER VALIDATION -- START"
if (($domain_name -eq ''))
{
	error_exit "ERROR:DOMAIN NAME NOT SET"	
}

if (($domain_username -eq ''))
{
	error_exit "ERROR:DOMAIN USER NAME NOT SET"	
}
else
{
	if(!(valid_string $domain_username))
	{ error_exit "$domain_username IS NOT A VALID STRING"}
}

if (($domain_password -eq ''))
{
	error_exit "ERROR:PASSWORD FOR DOMAIN USER NAME NOT SET"	
}
else
{
	if(!(valid_password $domain_password))
	{ error_exit "$domain_password IS NOT A VALID PASSWORD"}
}

if (($TargetName -eq ''))
{
	error_exit "ERROR:iSCI TARGET NAME NOT SET"	
}
else
{
	if(!(valid_string $TargetName))
	{ error_exit "$TargetName IS NOT A VALID STRING"}
}

if (($iSCSI_Hostname -eq ''))
{
	error_exit "ERROR:iSCI SERVER NAME NOT SET"	
}

if (($iSCSI_Target_IP -eq ''))
{
	error_exit "ERROR:iSCSI TARGET IP ADDRESS NOT SET"	
}
else
{
	if(!(valid_ip $iSCSI_Target_IP))
	{ error_exit "$iSCSI_Target_IP IS NOT A VALID IP"}
}

if (($cluster_name -eq ''))
{
	error_exit "ERROR:CLUSTER NAME NOT SET"	
}
else
{
	if(!(valid_string $cluster_name))
	{ error_exit "$cluster_name IS NOT A VALID STRING"}
}
if (($clust_iP -eq ''))
{
	error_exit "ERROR:IP ADDRESS FOR CLUSTER NOT SET"	
}
else
{
	if(!(valid_ip $clust_iP))
	{ error_exit "$clust_iP IS NOT A VALID IP"}
}
if (($ClusterServerRoleName -eq ''))
{
	error_exit "ERROR:CLUSTER SERVRE ROLE NAME NOT SET"	
}
else
{
	if(!(valid_string $ClusterServerRoleName))
	{ error_exit "$ClusterServerRoleName IS NOT A VALID STRING"}
}
if (($ClusterResourceName -eq ''))
{
	error_exit "ERROR:CLUSTER RESOURCE NAME NOT SET"	
}
else
{
	if(!(valid_string $ClusterResourceName))
	{ error_exit "$ClusterResourceName IS NOT A VALID STRING"}
}
if (($ClusterServerRoleIP -eq ''))
{
	error_exit "ERROR:IP ADDRESS FOR CLUSTER SERVER ROLE NOT SET"	
}
else
{
	if(!(valid_ip $ClusterServerRoleIP))
	{ error_exit "$ClusterServerRoleIP IS NOT A VALID IP"}
}
if (($ClusterServerRoleStorage -eq ''))
{
	error_exit "ERROR:STORAGE DISK FOR CLUSTER SERVER ROLE NOT SET"	
}
echo "PARAMETERS VALIDATION -- DONE"

# ENABLE PS-REMOTING ON EACH NODE TO EXECUTE COMMAND ON REMOTE MACHINE
echo "ENABLING PS-REMOTING..."
netsh advfirewall firewall add rule name="Windows Remote Management (HTTP-In)" dir=in action=allow service=any enable=yes profile=any localport=5985 protocol=tcp
$error.clear()
schtasks /CREATE /TN "EnablePS-Remoting" /SC MINUTE /MO 1 /RL HIGHEST /RU $domain_name\$domain_username /RP $domain_password /TR "powershell -Command Enable-PSRemoting -Force" /F
check_error "ERROR:WHILE CREATING SCHEDULED TASK TO ENABLE PS-REMOTING"
$error.clear()
schtasks /RUN /TN "EnablePS-Remoting"
check_error "ERROR:WHILE RUNNING SCHEDULED TASK TO ENABLE PS-REMOTING"

# CREATE AN ISCSI TARGET ONTO THE ISCSI SERVER AND ASSIGN THE DISKS TO THE CREATED TARGET.THESE DISKS WILL BE USED FOR THE CLUSTERING PURPOSES
$MainNode=[System.Net.Dns]::GetHostName()
if($initiator_hostname[0] -eq $MainNode)
{
	# GETTING THE IP ADDRESSES OF EACH NODE INTO A VARIABLE "IPAdd". THIS VARIABLE IS USED FOR CONNECTING THE ISCSI INITIATOR WITH THE ISCSI TARGET
	for ($i=0; $i -lt $initiator_ip.length -1; $i++) {
		$IPAdd += "IPAddress:" + $initiator_ip[$i] + ",";
	}
	$IPAdd += "IPAddress:" + $initiator_ip[$i]

	Add-Content "c:\disk.ps1" "Import-Module MicrosoftIscsiTarget"
	# CREATE A NEW iSCSI TARGET NAMED "$TargetName" INTO THE iSCSI SERVER "$iSCSI_Hostname"
	Add-Content "c:\disk.ps1" "New-IscsiServerTarget -TargetName `"$TargetName`" -InitiatorId $IPAdd -ComputerName $iSCSI_Hostname"
	# ASSIGN THE DISKS TO THE ISCSI TARGET
	# DISK 1 WILL BE USED FOR FAILOVER CLUSTER DISK
	# DISK 2 WILL BE USED FOR MSDTC SERVICE CREATED ON THE CLUSTER
	# DISK 3 WILL USED FOR THE SQL FAILOVER CLUSTER DISK
	Add-Content "c:\disk.ps1" "Add-VirtualDiskTargetMapping -TargetName `"$TargetName`" -DevicePath `"c:\disks\disk1.vhd`" -ComputerName $iSCSI_Hostname"
	Add-Content "c:\disk.ps1" "Add-VirtualDiskTargetMapping -TargetName `"$TargetName`" -DevicePath `"c:\disks\disk2.vhd`" -ComputerName $iSCSI_Hostname"
	Add-Content "c:\disk.ps1" "Add-VirtualDiskTargetMapping -TargetName `"$TargetName`" -DevicePath `"c:\disks\disk3.vhd`" -ComputerName $iSCSI_Hostname"

	$domainPASS = convertto-securestring "$domain_password" -asplaintext force
	$domainCredential = New-Object -TypeName System.Management.Automation.PSCredential -ArgumentList "$domain_name\$domain_username",$domainPASS
	echo "CREATING ISCSI TARGET ON ISCSI SERVER AND ASSIGNING THE DISK TO IT..."
	# THIS COMMAND EXECUTES ON THE ISCSI SERVER WITH THE DOMAIN CREDENTIAL TO CREATE AN ISCSI TARGET.
	$error.clear()
	invoke-command -computername $iSCSI_Hostname -filepath C:\disk.ps1 -Credential $domainCredential
	check_error "ERROR:WHILE CREATING iSCSI TARGET"
	echo "iSCSI TARGET CREATED SUCCESSFULLY"
}

# AFTER CREATING THE ISCSI TARGET, CONNECT THE ISCSI INITIATOR OF EACH NODE INCLUDING THE FIRST NODE TO THE ISCSI TARGET
echo "CONFIGURING ISCSI INITIATOR..."
# ENABLE THE ISCSI INITIATOR START UP TO "Automatic"
$error.clear()
Set-Service -Name msiscsi -StartupType Automatic
check_error "ERROR:WHILE ENABLING THE ISCSI INITIATOR"
# START THE MICROSOFT ISCSI SERVICE
$error.clear()
Start-Service msiscsi
check_error "ERROR:WHILE STARTING iSCSI SERVICE"
# CHECK WHETHER ISCSI TARGET EXISTS OR NOT
[ScriptBlock] $global:Check_iSCSITarget_ScriptBlock = {

	param($TargetName, $iSCSI_Hostname)
	import-module MicrosoftIscsiTarget
	[string]$global:target=Get-IscsiServerTarget -TargetName $TargetName -ComputerName $iSCSI_Hostname | Select-Object TargetName | Select-String "TargetName"
	return $target
}
$wait_interval = 30
$wait_limit = 300
for ($wait_c=0; $wait_c -le $wait_limit; ) {

	$domainPASS = convertto-securestring "$domain_password" -asplaintext force
	$domainCredential = New-Object -TypeName System.Management.Automation.PSCredential -ArgumentList "$domain_name\$domain_username",$domainPASS
	$error.clear()
	invoke-command -computername $iSCSI_Hostname -scriptblock $Check_iSCSITarget_ScriptBlock -argumentlist $TargetName, $iSCSI_Hostname -Credential $domainCredential
	if ($error.count -eq 0)
	{
		echo "iSCSI TARGET EXISTS"
		# FINAL WAIT FOR THINGS TO SETTLE DOWN
		Start-Sleep $wait_interval
		break;
	}
	else
	{
		echo "WAITING FOR ISCSI TARGET CREATION TO COMPLETE..."
		Start-Sleep $wait_interval
		$wait_c = $wait_c + $wait_interval
		if ($wait_c -ge $wait_limit) {
			echo "Abort. ISCSI TARGET CREATION DID NOT FINISH WITHIN $wait_limit seconds."
			exit 1
		}
	}
}
# ADD ISCSI TARGET
$error.clear()
iscsicli.exe QAddTargetPortal $iSCSI_Target_IP
check_error "ERROR:WHILE ADDING iSCSI TARGET"
# LOGIN TO TARGET
$error.clear()
iscsicli.exe QLoginTarget "iqn.1991-05.com.microsoft:$iSCSI_Hostname-$TargetName-target"
check_error "ERROR:WHILE LOGIN TO THE iSCSI TARGET"
# SET UP A PERSISTENT CONNECTION (RECONNECTS AFTER REBOOT SERVER)
$error.clear()
iscsicli.exe PersistentLoginTarget "iqn.1991-05.com.microsoft:$iSCSI_Hostname-$TargetName-target" T * * * * * * * * * * * * * * * 0
check_error "ERROR:WHILE SETTING UP PERSISTENT CONNECTION"
echo "iSCSI INITIATOR CONFIGURED SUCCESSFULLY"

######################################################################################################################
# Purpose: 1. This script installs the failover cluster feature on each node                                         #
#          2. This script creates a Failover Cluster                                                                 #
# Note: 1. This script assumes an iSCSI target is already created having at least three virtual disks assigned       #
#       2. This Script assumes the first node is responsible for creating the failover cluster                       #
######################################################################################################################

# PROPERTIES
$cluster_log="C:\Windows\Cluster\Reports\validate-ClusterLog.mht"
$domainPASS = convertto-securestring "$domain_password" -asplaintext force
$domainCredential = New-Object -TypeName System.Management.Automation.PSCredential -ArgumentList "$domain_name\$domain_username",$domainPASS

# VALIDATION FUNCTIONS 
Function check_error
{
	if ($error.count -gt 0)
	{ 
		error_exit $args[0] 
	}
}

Function error_exit
{
	echo $args[0]
	exit 1
}

# INTERNAL SCRIPT BLOCKS
# TO CHECK WHETHER A CLUSTER EXIST OR NOT
[ScriptBlock] $global:Check_Cluster_existence_ScriptBlock = {

	param($cluster_name)
	import-module failoverclusters
	[string]$global:cluster=Get-Cluster | Where-Object { $_.Name -eq "$cluster_name" }
	return $cluster
}
#TO CHECK WHETHER A CLUSTER SERVICE EXIST OR NOT
[ScriptBlock] $global:Check_MSDTC_existence_ScriptBlock = {

	param($ClusterServerRoleName)
	import-module failoverclusters
	[string]$global:service=Get-ClusterGroup | Where-Object { $_.Name -eq "$ClusterServerRoleName" }
	return $service
}

# INSTALL THE FAILOVER CLUSTER FEATURE ON EACH NODE
echo "INSTALLING FAILOVER-CLUSTER..."
$error.clear()
servermanagercmd -install failover-clustering
check_error "ERROR:WHILE INSTALLING FAILOVER CLUSTER FEATURE"
echo "FAILOVER CLUSTER FEATURE INSTALLED SUCCESSFULLY"

# CREATE THE FAILOVER CLUSTER ON FIRST NODE
$MainNode=[System.Net.Dns]::GetHostName()
if($initiator_hostname[0] -eq $MainNode)
{	
	for ($i=0; $i -lt $initiator_ip.length -1; $i++) {
		# THIS VARIABLE CONTAINS THE HOSTNAME OF EACH NODE.THIS VALUE WILL USED FOR ADDING THE NODE TO THE CLUSTER
		$nodes += $initiator_hostname[$i] + ","; 
	}
	$nodes += $initiator_hostname[$i]

	Add-Content "c:\validate.ps1" 'import-module failoverclusters'
	# VALIDATE THE CLUSTER AND SAVING THE VALIDATION REPORT TO A FILE "$cluster_log"
	Add-Content "c:\validate.ps1" "test-cluster -node $nodes -ReportName $cluster_log" 
	Add-Content "c:\cluster.ps1" 'import-module failoverclusters'
	# CREATE A NEW CLUSTER HAVING STATIC IP "clust_ip"
	Add-Content "c:\cluster.ps1" "NEW-CLUSTER NAME `"$cluster_name`" NODE $nodes -STATICADDRESS $clust_ip"

	Add-Content "c:\msdtc.ps1" "import-module failoverclusters"
	# CREATE A NEW SERVER ROLE NAMED "$ClusterServerRoleName" HAVING "$ClusterServerRoleStorage" AS ITS STORAGE AND "$ClusterServerRoleIP" AS ITS IP
	Add-Content "c:\msdtc.ps1" "Add-ClusterServerRole -Name $ClusterServerRoleName -Storage `"$ClusterServerRoleStorage`" -StaticAddress $ClusterServerRoleIP"
	# ADD THE MSDTC SERVICE NAMED "$ClusterResourceName" TO THE CREATED SERVER ROLE "$ClusterServerRoleName AND SET THE RESOURCE TYPE TO "Distributed Transaction Coordinator"" 
	Add-Content "c:\msdtc.ps1" "Get-ClusterGroup $ClusterServerRoleName | Add-ClusterResource -Name $ClusterResourceName -ResourceType `"Distributed Transaction Coordinator`""
	# CREATE DEPENDENCIES FOR THE DTC GROUP 
	Add-Content "c:\msdtc.ps1" "Add-ClusterResourceDependency $ClusterResourceName $ClusterServerRoleName"
	Add-Content "c:\msdtc.ps1" "Add-ClusterResourceDependency $ClusterResourceName `"$ClusterServerRoleStorage`""
	# START DTC GROUP
	Add-Content "c:\msdtc.ps1" "Start-ClusterGroup $ClusterServerRoleName"
	# SET THE OWNER NODES FOR THE CREATED DTC GROUP
	Add-Content "c:\msdtc.ps1" "Set-ClusterOwnerNode -Group $ClusterServerRoleName -Owners $nodes"
	# SET THE ALLOW FAILBACK PROPERTY SO THAT WE CAN MOVE THIS DTC GROUP FROM ONE NODE TO ANOTHER
	Add-Content "c:\msdtc.ps1" "cluster.exe group `"$ClusterServerRoleName`" /prop AutoFailbackType=`"1`""

	# CREATE SCHEDULED TASKS TO ACCOMPLISH VALIDATION,CLUSTER CREATION AND SERVICE CREATION RESPECTIVELY.
	# NOTE : TO RESOLVE THE POP UP WINDOW ISSUE, WE HAVE TO CREATE THESE SCHEDULED TASKS.
	$error.clear()
	schtasks.exe /CREATE /TN "Validate-Failover-Cluster" /SC MONTHLY /MO first /D SUN /RL HIGHEST /RU $domain_name\$domain_username /RP $domain_password /TR "powershell -noprofile -command &{C:\validate.ps1} -WindowStyle Hidden" /F
	check_error "ERROR:WHILE CREATING SCHEDULED TASK FOR VALIDATION"

	# RUN THE SCHEDULED TASK "Validate-Failover-Cluster"" FOR VALIDATING THE FAILOVER CLUSTER
	echo "VALIDATING THE CLUSTER..."
	$error.clear()
	schtasks.exe /RUN /TN "Validate-Failover-Cluster"
	check_error "ERROR:WHILE RUNNING THE SCHEDULED TASK FOR VALIDATION"

	# WAIT UNTILL CLUSTER VALIDATION IS DONE
	$wait_interval = 60
	$wait_limit = 1800
	$wait_c=0
	do
	{
		# CHECKING FOR SUCCESSFULL VALIDATION OF FAILOVER CLUSTER.
		# IF CLUSTER IS VALIDATED SUCCESSFULLY THEN A FILE IS CREATED WITH AN EXTENSION OF .mht IN THE LOCATION "C:\Windows\Cluster\Reports\"
		if (test-path -path "C:\Windows\Cluster\Reports\*" -Include *.mht) {
			$result=ls C:\Windows\Cluster\Reports\* -Include *.mht -Force | Get-Content | Select-String "Failed"
			if(!$result)
			{
				# Final wait for things to settle down
				Start-Sleep $wait_interval
				echo "FAILOVER CLUSTER VALIDATED SUCCESSFULLY" 
				break
			}
			else
			{
				echo "ERROR:FAILOVER CLUSTER VALIDATION FAILED"
				exit 1
			}
		}
		echo "WAITING FOR FAILOVER CLUSTER VALIDATION TO COMPLETE..."
		Start-Sleep $wait_interval
		$wait_c = $wait_c + $wait_interval
		if ($wait_c -ge $wait_limit) {
			echo "Abort. FAILOVER CLUSTER VALIDATION DID NOT FINISH WITHIN $wait_limit seconds."
			exit 1
		}
	}while ($wait_c -le $wait_limit)

	$error.clear()
	schtasks.exe /CREATE /TN "Create-Failover-Cluster" /SC MONTHLY /MO first /D SUN /RL HIGHEST /RU $domain_name\$domain_username /RP $domain_password /TR "powershell -noprofile -command &{C:\cluster.ps1} -WindowStyle Hidden" /F
	check_error "ERROR:WHILE CREATING SCHEDULED TAKS FOR CLUSTER CREATION"

	# RUN THE SCHEDULED TASK "Create-Failover-Cluster" FOR CREATING THE FAILOVER CLUSTER
	echo "CREATING FAILOVER CLUSTER..."
	$error.clear()
	schtasks.exe /RUN /TN "Create-Failover-Cluster"
	check_error "ERROR:WHILE RUNNING THE SCHEDULED TASK FOR CREATING CLUSTER"

	# WAIT UNTILL FAILOVER CLUSTER CREATION IS DONE
	$wait_c=0
	do {
		echo "WAITING FOR FAILOVER CLUSTER CREATION TO COMPLETE..."
		Start-Sleep $wait_interval
		# CHECKING FOR SUCCESSFULL CREATION OF FAILOVER CLUSTER
		# CHECK WHETHER THE SHEDULED TASK "Create-Failover-Cluster" IS COMPLETED OR NOT
		# IF IT IS COMPLETED THEN THE $status_code CONTAINS "0" ELSE "1"
		$status_code=schtasks.exe /query /fo list /tn "Create-Failover-Cluster" | find.exe /c `"Running`"
		if("$status_code" -eq "0")
		{
			$result=invoke-command -computername $initiator_hostname[0] -scriptblock $Check_Cluster_existence_ScriptBlock -argumentlist $cluster_name -Credential $domainCredential
			if ($result)
			{
				# FINAL WAIT FOR THINGS TO SETTLE DOWN
				Start-Sleep $wait_interval
				echo "FAILOVER CLUSTER CREATED SUCCESSFULLY"
				break;
			}
			else
			{
				echo "ERROR: FAILOVER CLUSTER NOT CREATED"
				exit 1
			}
		}
		$wait_c = $wait_c + $wait_interval
		if ($wait_c -ge $wait_limit) {
			echo "Abort. FAILOVER CLUSTER CREATION DID NOT FINISH WITHIN $wait_limit seconds."
			exit 1
		}
	}while ($wait_c -le $wait_limit)

	$error.clear()
	schtasks.exe /CREATE /TN "Configuring-MSDTC" /SC MONTHLY /MO first /D SUN /RL HIGHEST /RU $domain_name\$domain_username /RP $domain_password /TR "powershell -noprofile -command &{C:\msdtc.ps1} -WindowStyle Hidden" /F
	check_error "ERROR:WHILE CREATING SCHEDULED TAKS FOR CREATING MSDTC SERVICE"

	# RUN THE SCHEDULED TASK "Configuring-MSDTC" FOR CREATING MSDTC SERVICE ON FAILOVER CLUSTER
	echo "CONFIGURING MSDTC..."
	$error.clear()
	schtasks.exe /RUN /TN "Configuring-MSDTC"
	check_error "ERROR:WHILE RUNNING THE SCHEDULED TASK FOR CREATING MSDTC SERVICE ON THE FAILOVER CLUSTER"

	# WAIT UNTILL MSDTC SERVICE IS CREATED	
	$wait_c=0
	do {
		echo "WAITING FOR FAILOVER CLUSTER SERVICE CREATION TO COMPLETE..."
		Start-Sleep $wait_interval
		# CHECKING FOR SUCCESSFULL CREATION OF FAILOVER CLUSTER SERVICE 
		# CHECK WHETHER THE SHEDULED TASK "Configuring-MSDTC" IS COMPLETED OR NOT
		# IF IT IS COMPLETED THEN THE $status_code CONTAINS "0" ELSE "1"
		$status_code=schtasks.exe /query /fo list /tn "Configuring-MSDTC" | find.exe /c `"Running`"
		if("$status_code" -eq "0")
		{
			$result=invoke-command -computername $initiator_hostname[0] -scriptblock $Check_MSDTC_existence_ScriptBlock -argumentlist $ClusterServerRoleName -Credential $domainCredential
			if ($result)
			{
				# FINAL WAIT FOR THINGS TO SETTLE DOWN
				Start-Sleep $wait_interval
				echo "FAILOVER CLUSTER SERVICE CREATED SUCCESSFULLY"
				break;
			}
			else
			{
				echo "ERROR: FAILOVER CLUSTER SERVICE NOT CREATED"
				exit 1
			}
		}
		$wait_c = $wait_c + $wait_interval
		if ($wait_c -ge $wait_limit) {
			echo "Abort. FAILOVER CLUSTER SERVICE CREATION DID NOT FINISH WITHIN $wait_limit seconds."
			exit 1
		}
	}while ($wait_c -le $wait_limit)
	# DELETE ALL THE CREATED TASKS
	schtasks.exe /DELETE /F /TN "Validate-Failover-Cluster"
	schtasks.exe /DELETE /F /TN "Create-Failover-Cluster"
	schtasks.exe /DELETE /F /TN "Configuring-MSDTC"
}
# ON EACH NODE OTHER THAN THE FIRST NODE, WAIT UNTILL FAILOVER CLUSTER IS CREATED
else
{
	$wait_interval = 60
	$wait_limit = 1800
	$wait_c=0
	do
	{
		# CHECKING FOR SUCCESSFULL VALIDATION OF FAILOVER CLUSTER.
		# IF CLUSTER IS VALIDATED SUCCESSFULLY THEN A FILE IS CREATED WITH AN EXTENSION OF .mht IN THE LOCATION "C:\Windows\Cluster\Reports\"
		if (test-path -path "C:\Windows\Cluster\Reports\*" -Include *.mht) {
			$result=ls C:\Windows\Cluster\Reports\* -Include *.mht -Force | Get-Content | Select-String "Failed"
			if(!$result)
			{
				# Final wait for things to settle down
				Start-Sleep $wait_interval
				echo "FAILOVER CLUSTER VALIDATED SUCCESSFULLY" 
				break
			}
			else
			{
				echo "ERROR:FAILOVER CLUSTER VALIDATION FAILED"
				exit 1
			}
		}
		echo "WAITING FOR FAILOVER CLUSTER VALIDATION TO COMPLETE..."
		Start-Sleep $wait_interval
		$wait_c = $wait_c + $wait_interval
		if ($wait_c -ge $wait_limit) {
			echo "Abort. FAILOVER CLUSTER VALIDATION DID NOT FINISH WITHIN $wait_limit seconds."
			exit 1
		}
	}while ($wait_c -le $wait_limit)

	# WAIT UNTILL FAILOVER CLUSTER CREATION IS DONE
	$wait_c=0
	do {
		echo "WAITING FOR FAILOVER CLUSTER CREATION TO COMPLETE..."
		Start-Sleep $wait_interval
		# CHECKING FOR SUCCESSFULL CREATION OF FAILOVER CLUSTER
		# CHECK WHETHER THE SHEDULED TASK "Create-Failover-Cluster" IS COMPLETED OR NOT
		# IF IT IS COMPLETED THEN THE $status_code CONTAINS "0" ELSE "1"
		$status_code=schtasks.exe /query /s $initiator_hostname[0] /fo list /tn "Create-Failover-Cluster" /U $domain_name\$domain_username /P $domain_password | find.exe /c `"Running`"
		if("$status_code" -eq "0")
		{
			$result=invoke-command -computername $initiator_hostname[0] -scriptblock $Check_Cluster_existence_ScriptBlock -argumentlist $cluster_name -Credential $domainCredential
			if ($result)
			{
				# FINAL WAIT FOR THINGS TO SETTLE DOWN
				Start-Sleep $wait_interval
				echo "FAILOVER CLUSTER CREATED SUCCESSFULLY"
				break;
			}
			else
			{
				echo "ERROR: FAILOVER CLUSTER NOT CREATED"
				exit 1
			}
		}
		$wait_c = $wait_c + $wait_interval
		if ($wait_c -ge $wait_limit) {
			echo "Abort. FAILOVER CLUSTER CREATION DID NOT FINISH WITHIN $wait_limit seconds."
			exit 1
		}
	}while ($wait_c -le $wait_limit)

	# WAIT UNTILL MSDTC SERVICE IS CREATED
	$wait_c=0
	do {
		echo "WAITING FOR FAILOVER CLUSTER SERVICE CREATION TO COMPLETE..."
		Start-Sleep $wait_interval
		# CHECKING FOR SUCCESSFULL CREATION OF FAILOVER CLUSTER SERVICE 
		# CHECK WHETHER THE SHEDULED TASK "Configuring-MSDTC" IS COMPLETED OR NOT
		# IF IT IS COMPLETED THEN THE $status_code CONTAINS "0" ELSE "1"
		$status_code=schtasks.exe /query /s $initiator_hostname[0] /fo list /tn "Configuring-MSDTC" /U $domain_name\$domain_username /P $domain_password | find.exe /c `"Running`"
		if("$status_code" -eq "0")
		{
			$result=invoke-command -computername $initiator_hostname[0] -scriptblock $Check_MSDTC_existence_ScriptBlock -argumentlist $ClusterServerRoleName -Credential $domainCredential
			if ($result)
			{
				# FINAL WAIT FOR THINGS TO SETTLE DOWN
				Start-Sleep $wait_interval
				echo "FAILOVER CLUSTER SERVICE CREATED SUCCESSFULLY"
				break;
			}
			else
			{
				echo "ERROR: FAILOVER CLUSTER SERVICE NOT CREATED"
				exit 1
			}
		}
		$wait_c = $wait_c + $wait_interval
		if ($wait_c -ge $wait_limit) {
			echo "Abort. FAILOVER CLUSTER SERVICE CREATION DID NOT FINISH WITHIN $wait_limit seconds."
			exit 1
		}
	}while ($wait_c -le $wait_limit)
}

