# Purpose: 1. This script installs the sql failover cluster feature on first node                                    #
#          2. This script adds node(s) other than first node into the sql Failover Cluster                           #
# Note: 1. This script assumes failover cluster is already created                                                   #
#       2. This Script assumes the first node is responsible for creating the sql failover cluster                   #
######################################################################################################################

# PROPERTIES
# VARIABLE FOR SQL FAILOVER CLUSTER GROUP
$FAILOVERCLUSTERGROUP="SQL Server ($instanceName)"
# VARIABLE FOR SQL FAILOVER CLUSTER IP ADDRESS
$FAILOVERCLUSTERIPADDRESSES="IPv4;$SQL_Cluster_ip;$SQL_Cluster_Network;255.255.255.0"
$domain_account="$domain_name\$domain_username"
$sql_root_folder="$InstanceDir"+"\MSSQL11.$instanceName"
$mssql_bin = $sql_root_folder + "\MSSQL\Binn\sqlservr.exe"

# VALIDATION FUNCTIONS 
Function check_error
{
	if ($error.count -gt 0)
	{ 
		error_exit $args[0] 
	}
}

Function error_exit
{
	echo $args[0]
	exit 1
}

Function valid_string
{ 
	$STR = $args[0]
    $ValidSTR = $true
    $ValidSTR = ($STR -match "^[A-Za-z]{1,}[A-Za-z0-9_-]{1,}$")
    if(-not $ValidSTR){return $false}
	return $ValidSTR
}

Function valid_password
{ 
	$STR = $args[0]
    $ValidSTR = $true
    $ValidSTR = ($STR -match "^[A-Za-z]{1,}[0-9_@$%^#+=]{0,}[A-Za-z0-9]{0,}$")
    if(-not $ValidSTR){return $false}
	return $ValidSTR
}

Function valid_ip
{
	$IP = $args[0]
    $ValidIP = $true

    <#Test format and leading zero (technically not necessary to test for
    leading zero as this function strips the leading zero when converting to [int]#>
    $ValidIP = ($IP -match "^[1-9]\d{0,2}\.\d{1,3}\.\d{1,3}\.\d{1,3}$") 
    if(-not $ValidIP){return $false}

    $myip = $IP -split "\." #Each octet is element of an array
    $ValidIP = ([int]$myip[0] -gt 0 -and [int]$myip[0] -le 255) #First octet is between 1-255
    if(-not $ValidIP){return $false}

    for($i = 1;$i -le 3;$i++){
        $ValidIP = ([int]$myip[$i] -ge 0 -and [int]$myip[$i] -le 255) #Remain octets between 0-255
        if(-not $ValidIP){return $false}
    }
    return $ValidIP #$ValidIP will always be true at this point.
}

