	{
		error_exit "ERROR:URL FOR SQL CORE INSTALLER NOT SET"	
	}
	if (($SQLFULL_x64_ENU_Lang -eq ''))
	{
		error_exit "ERROR:URL FOR SQL LANG INSTALLER NOT SET"	
	}
}
echo "PARAMETERS VALIDATION -- DONE"

# NOTE: THE ".NET Framework 3.5 SP1 and Windows Installer 4.5" ARE REQUIRED COMPONENTS TO INSTALL SQL2008/2012
# INSTALL PREREQUISITES FOR SQL SERVER 2012
echo "INSTALLING PRE-REQUISITES..."
$error.clear()
Servermanagercmd -install Application-Server
check_error "ERROR:WHILE INSTALLING APPLICATION SERVER FEATURE"
$error.clear()
Servermanagercmd -install AS-Dist-Transaction AS-Ent-Services -allSubFeatures
check_error "ERROR:WHILE INSTALLING PRE-REQUISITES"
echo "SQL PRE-REQUISITES INSTALLED SUCCESSFULLY"

# DOWNLOAD THE SQL INSTALLER FILES INTO THE DIRECTORY "c:\sqlSrver_2012"
mkdir "c:\sqlSrver_2012"
echo "DOWNLOADING SQL 2012 SERVER INSTALLERS..."
$wc = new-object System.Net.WebClient
$error.clear()
$wc.Downloadfile($SQLFULL_x64_ENU_Core,"c:\sqlSrver_2012\SQLFULL_x64_ENU_Core.box")
check_error "ERROR:WHILE DOWNLOADING THE SQL INSTALLER"
$wc.Downloadfile($SQLFULL_x64_ENU_Install,"c:\sqlSrver_2012\SQLFULL_x64_ENU_Install.exe")
check_error "ERROR:WHILE DOWNLOADING THE SQL INSTALLER"
$wc.Downloadfile($SQLFULL_x64_ENU_Lang,"c:\sqlSrver_2012\SQLFULL_x64_ENU_Lang.box")
check_error "ERROR:WHILE DOWNLOADING THE SQL INSTALLER"
echo "SQL 2012 SERVER INSTALLERS DOWNLOADED SUCCESSFULLY"

# NOTE: TO REDUCE THE DOWNLOAD TIME, DOWNLOADED THE SQL INSTALER FILES ARE DOWNOADED ON THE HTTP SERVER "10.140.16.75"
# EXTRACT THE SQL INSTALLER
echo "EXTRACTING THE INSTALLER..."
$error.clear()
start "C:\sqlSrver_2012\SQLFULL_x64_ENU_Install.exe" -WindowStyle Hidden -wait
check_error "ERROR:WHILE EXTRACTING THE SQL INSTALLER"
echo "SQL INSTALLERS EXTRACTED SUCCESSFULLY"
# DELETE THE INSTALLER FILES TO SAVE THE DISK SPACE
$error.clear()
remove-item C:\sqlSrver_2012\*.* -Force
check_error "ERROR:WHILE REMOVING THE SQL INSTALLER"

# INSTALL THE SQL FAILOVER CLUSTER ON THE FIRST NODE
$MainNode=[System.Net.Dns]::GetHostName()
if($nodes_hostname[0] -eq $MainNode)
{
	# FUNCTION TO INSTALL SQL FAILOVER CLUSTER ON FIRST NODE
	Function Install
	{
		param([string]$SQL_Installer_path, [string]$instanceName, [string] $InstanceDir, [string] $FAILOVERCLUSTERGROUP, [string] $failoverclusterdisks, [string] $FAILOVERCLUSTERIPADDRESSES, [string] $domain_account, [string] $domain_password, [string]$SAPWD)

		add-content "C:\InstallSQL.ps1" "$SQL_Installer_path\setup.exe /Q /SkipRules=Cluster_VerifyForErrors /ACTION=InstallFailoverCluster /IACCEPTSQLSERVERLICENSETERMS /INDICATEPROGRESS /FEATURES=SQL,SSMS /INSTANCENAME=$instanceName /INSTANCEID=$instanceName /INSTANCEDIR=`"$InstanceDir`" /FAILOVERCLUSTERGROUP=`"$FAILOVERCLUSTERGROUP`" /FAILOVERCLUSTERNETWORKNAME=$instanceName /failoverclusterdisks=`"$failoverclusterdisks`" /FAILOVERCLUSTERIPADDRESSES=`"$FAILOVERCLUSTERIPADDRESSES`" /SQMREPORTING=0 /ERRORREPORTING=0 /UpdateEnabled=0 /AGTSVCACCOUNT=$domain_account /AGTSVCPASSWORD=$domain_password /SQLSVCACCOUNT=$domain_account /SQLSVCPASSWORD=$domain_password /SQLSYSADMINACCOUNTS=$domain_account /SECURITYMODE=`"SQL`" /SAPWD=$SAPWD"

		# CREATE SHCEDULED TASK "Install-SQL" TO INSTALL SQL FAILOVER CLUSTER
		# NOTE : TO RESOLVE THE POP UP WINDOW ISSUE, WE HAVE TO CREATE THE SCHEDULED TASK
		$error.clear()
		schtasks /CREATE /TN "Install-SQL" /SC MONTHLY /MO first /D SUN /RL HIGHEST /RU $domain_account /RP $domain_password /TR "powershell -noprofile -command &{C:\InstallSQL.ps1} -WindowStyle Hidden" /F
		check_error "ERROR:WHILE CREATING SCHEDULED TASK TO INSTALL SQL FAILOVER CLUSTER"

		# RUN THE SCHEDULED TASK "Install-SQL" TO INSTALL SQL FAILOVER CLUSTER
		echo "INSTALLING SQL CLUSTER..."
		$error.clear()
		schtasks /RUN /TN "Install-SQL"
		check_error "ERROR:WHILE RUNNING THE SCHEDULED TASK TO INSTALL THE SQL FAILOVER CLUSTER"
		# Note: SQL FAILOVER CLUSTER INSTALLATION TAKES ALMOST 1 HOUR TO COMPLETE

		# WAIT FOR SQL FAILOVER CLUSTER INSTALLATION TO COMPLETE
		$wait_interval = 60
		$wait_limit = 5400
		for ($wait_c=0; $wait_c -le $wait_limit; ) {

			echo "WAITING FOR SQL FAILOVER CLUSTER INSTALLATION TO COMPLETE..."
			Start-Sleep $wait_interval

			# CHECKING FOR SUCCESSFULL INSTALLATION OF SQL FAILOVER CLUSTER.
			# CHECK WHETHER THE SCHEDULED TASK "Install-SQL" IS COMPLETED OR NOT.
			# IF IT IS COMPLETED THEN THE $status_code CONTAINS "0" ELSE "1"
			$status_code=schtasks /query /fo list /tn "Install-SQL" | find.exe /c `"Running`"
			if("$status_code" -eq "0")
			{
				# IF SQL FAILOVER CLUSTER INSTALLED SUCCESSFULLY THEN AN EXECUTABLE FILE "sqlservr.exe" IS CREATED AT THE LOCATION "$sql_root_folder + "\MSSQL\Binn\"
				if (Test-Path $mssql_bin) {
					# FINAL WAIT FOR THINGS TO SETTLE DOWN
					Start-Sleep $wait_interval
					echo "SQL FAILOVER CLUSTER INSTALLED SUCCESSFULLY"
					break
				}
				else
				{
					echo "ERROR:WHILE INSTALLING THE SQL FAILOVER CLUSTER"
					exit 1
				}
			}

			$wait_c = $wait_c + $wait_interval
			if ($wait_c -ge $wait_limit) {
				echo "Abort. SQL FAILOVER CLUSTER INSTALLATION DID NOT FINISH WITHIN $wait_limit seconds."
				exit 1
			}
		}
	}
	# INVOKE THE FUNCTION "Install" TO INSTALL SQL FAILOVER CLUSTER
	Install "$SQL_Installer_path" "$instanceName" "$InstanceDir" "$FAILOVERCLUSTERGROUP" "$failoverclusterdisks" "$FAILOVERCLUSTERIPADDRESSES" "$domain_account" "$domain_password" "$SAPWD"
}
# ON EACH NODE OTHER THAN FIRST NODE EXECUTE ADDNODE COMMAND TO ADD NODE(S) INTO THE CREATED SQL FAILOVER CLUSTER
else
{
	# FUNCTION TO ADD NODE TO TO ADD NODE(S) INTO THE CREATED SQL FAILOVER CLUSTER
	function addNode
	{
		param([string]$SQL_Installer_path, [string]$instanceName, [string] $FAILOVERCLUSTERGROUP, [string] $FAILOVERCLUSTERIPADDRESSES, [string] $domain_account, [string] $domain_password)

		# WAIT UNTILL SQL FAILOVER CLUSTER INSTALLATION IS COMPLETED ON THE FIRST NODE
		$wait_interval = 60
		$wait_limit = 5400
		for ($wait_c=0; $wait_c -le $wait_limit; ) {

			echo "WAITING FOR SQL INSTALLATION TO COMPLETE..."
			Start-Sleep $wait_interval

			# CHECKING WHETHER THE SCHEDULED TASK "Install-SQL" RUNNING ON THE FIRST NODE IS COMPLETED OR NOT.
			# IF IT IS COMPLETED THEN THE $status_code CONTAINS "0" ELSE "1"
			$status_code=schtasks /query /s $nodes_hostname[0] /fo list /tn "Install-SQL" /U $domain_name\$domain_username /P $domain_password | find.exe /c `"Running`"
			if("$status_code" -eq "0")
			{
				$domainPASS = convertto-securestring "$domain_password" -asplaintext force
				$domainCredential = New-Object -TypeName System.Management.Automation.PSCredential -ArgumentList "$domain_name\$domain_username",$domainPASS
				# IF THE SCHEDULED TASK "Install-SQL" RUNNING ON THE FIRST NODE IS COMPLETED THEN CHECK WHETHER THE FILE "$mssql_bin" EXISTS OR NOT
				if(invoke-command -computername $nodes_hostname[0] -scriptblock {param($mssql_bin);Test-Path $mssql_bin} -argumentlist $mssql_bin -Credential $domainCredential)
				{
					# FINAL WAIT FOR THINGS TO SETTLE DOWN
					Start-Sleep $wait_interval
					echo "SQL FAILOVER CLUSTER INSTALLED SUCCESSFULLY"
					break
				}
				else
				{
					echo "ERROR:WHILE INSTALLING THE SQL FAILOVER CLUSTER"
					exit 1
				}
			}

			$wait_c = $wait_c + $wait_interval
			if ($wait_c -ge $wait_limit) {
				echo "Abort. SQL INSTALLATION DID NOT FINISH WITHIN $wait_limit seconds."
				exit 1
			}
		}

		# CREATE SHCEDULED TASK "Add-Node" TO ADD NODE INTO THE CREATED SQL FAILOVER CLUSTER
		# NOTE : TO RESOLVE THE POP UP WINDOW ISSUE, WE HAVE TO CREATE THE SCHEDULED TASK.
		add-content "C:\AddSQLNode.ps1" "$SQL_Installer_path\setup.exe /Q /SkipRules=Cluster_VerifyForErrors /ACTION=AddNode /IACCEPTSQLSERVERLICENSETERMS /INDICATEPROGRESS /INSTANCENAME=$instanceName /FAILOVERCLUSTERGROUP=`"$FAILOVERCLUSTERGROUP`" /FAILOVERCLUSTERNETWORKNAME=$instanceName /FAILOVERCLUSTERIPADDRESSES=`"$FAILOVERCLUSTERIPADDRESSES`" /SQMREPORTING=0 /AGTSVCACCOUNT=$domain_account /AGTSVCPASSWORD=$domain_password /SQLSVCACCOUNT=$domain_account /SQLSVCPASSWORD=$domain_password"

		$error.clear()
		schtasks /CREATE /TN "Add-Node" /SC MONTHLY /MO first /D SUN /RL HIGHEST /RU $domain_account /RP $domain_password /TR "powershell -noprofile -command &{C:\AddSQLNode.ps1} -WindowStyle Hidden" /F
		check_error "ERROR:WHILE CREATING SCHEDULED TASK TO ADD NODE TO THE SQL FAILOVER CLUSTER"

		# RUN THE SCHEDULED TASK "Add-Node" TO ADD NODE INTO THE CREATED SQL FAILOVER CLUSTER
		echo "ADDING NODE TO THE SQL FAILOVER CLUSTER..."
		$error.clear()
		schtasks /RUN /TN "Add-Node"
		check_error "ERROR:WHILE RUNNING SCHEDULED TASK TO ADD NODE TO THE SQL FAILOVER CLUSTER"

		# WAIT UNTILL THE SCHEDULED TASK "Add-Node" IS COMPLETED
		for ($wait_c=0; $wait_c -le $wait_limit; ) {

			echo "WAITING FOR SQL NODE ADDITION TO COMPLETE..."
			Start-Sleep $wait_interval

			# CHECKING WHETHER THE SCHEDULED TASK "Add-Node" IS COMPLETED OR NOT.
			# IF IT IS COMPLETED THEN THE $status_code CONTAINS "0" ELSE "1"
			$status_code=schtasks /query /fo list /tn "Add-Node" | find.exe /c `"Running`"
			if("$status_code" -eq "0")
			{
				# IF THE SCHEDULED TASK "Add-Node" IS COMPLETED THEN CHECK WHETHER THE FILE "$mssql_bin" EXISTS OR NOT
				if (Test-Path $mssql_bin) {
					# FINAL WAIT FOR THINGS TO SETTLE DOWN
					Start-Sleep $wait_interval
					echo "SQL NODE ADDED SUCCESSFULLY TO SQL FAILOVER CLUSTER "
					break
				}
				else
				{
					echo "ERROR:WHILE ADDING THE SQL NODE TO THE SQL FAILOVER CLUSTER"
					exit 1
				}
			}

			$wait_c = $wait_c + $wait_interval
			if ($wait_c -ge $wait_limit) {
				echo "Abort. SQL FAILOVER CLUSTER NODE ADDTION DID NOT FINISH WITHIN $wait_limit seconds."
				exit 1
			}
		}
	}
	# INVOKE THE FUNCTION "addNode" TO ADD NODE
	addNode "$SQL_Installer_path" "$instanceName" "$FAILOVERCLUSTERGROUP" "$FAILOVERCLUSTERIPADDRESSES" "$domain_account" "$domain_password"
}

supported-os-details.exec-script:
$error.clear()
# Auto convert input properties to local Powershell variables

# Parsing JSON elements into Powershell object
function ParseJsonProps ($jsonFile)
{
 $jsonjsonCharCount = 0
 $newObjStart = $false
 $arrayStart = $false
 $tokenStart = $false
 $valueStart = $false
 $escapeStart = $false

 $jsonFileLen = $jsonFile.length
 $jsonObj = New-Object System.Text.StringBuilder

 ForEach($c in $jsonFile.ToCharArray()) {
    $jsonCharCount++
    $tmpStr = ""
    switch -regex ($c) {
        "{" { 
            if($newObjStart -eq $false) {
                $tmpStr = "(New-Object PSObject "
                $newObjStart = $true
            } else {
                $tmpStr = $c
            }
        }
        "}" {
            if($jsonCharCount -lt $jsonFileLen) {
                $tmpStr = $c
            } else {
                $tmpStr = ")"
            }
        }
        '"' {
            if($tokenStart -eq $false -and $valueStart -eq $false -and $arrayStart -eq $false) {
                $tmpStr = '| Add-Member -Passthru NoteProperty "'
            } else {
                if($escapeStart -eq $false) {
                    $tmpStr = $c
                } else {
                    $tmpStr = "```""
                    $escapeStart = $false
                }
            }
            $tokenStart = $true
        }
        ":" {
            if($valueStart -eq $true -or $arrayStart -eq $true) {
                $tmpStr = $c
            } else {
                $tmpStr = " "
            }
            $valueStart = $true
        }
        "," {
            if($arrayStart) {
                $tmpStr = $c
            } else {
                $valueStart = $false
                $tokenStart = $false
            }
        }	
        "\[" {
            $tmpStr = "@("
            $arrayStart = $true
        }
        "\]" {
            $tmpStr = ")"
            $arrayStart = $false
        }
        "``" {
            $tmpStr = "````"
        }
        "\\" {
            if($escapeStart -eq $false) {
                $escapeStart = $true
            } else {
                $tmpStr = $c
                $escapeStart = $false
            }
        }
        "\$" {
            if($escapeStart -eq $false) {
                $tmpStr = '`$'
            } else {
                $tmpStr = '`$'
                $escapeStart = $false
            }
        }
        "n" {
            if($escapeStart -eq $false) {
                $tmpStr = $c
            } else {
                $tmpStr = "``n"
                $escapeStart = $false
            }
        }
        "r" {
            if($escapeStart -eq $false) {
                $tmpStr = $c
            } else {
                $tmpStr = "``r"
                $escapeStart = $false
            }
        }
        "t" {
            if($escapeStart -eq $false) {
                $tmpStr = $c
            } else {
                $tmpStr = "``t"
                $escapeStart = $false
            }
        }
        "b" {
            if($escapeStart -eq $false) {
                $tmpStr = $c
            } else {
                $tmpStr = "``b"
                $escapeStart = $false
            }
        }
        default {
            if($escapeStart -eq $false) {
                $tmpStr = $c
            } else {
                $tmpStr = "\$c"
                $escapeStart = $false
            }
            #$tmpStr = $c
            #if($escapeStart -eq $true) {
            #    $escapeStart = $false
            #}
        }
    }                        
    [void] $jsonObj.Append($tmpStr)
 }

 $jsonObj | Invoke-Expression
}

$vmw_appd_json_file = Get-Content "$($Args[1])"
$vmw_appd_json_obj = ParseJsonProps $vmw_appd_json_file
