# CREATE AN ISCSI TARGET ONTO THE ISCSI SERVER AND ASSIGN THE DISKS TO THE CREATED TARGET.THESE DISKS WILL BE USED FOR THE CLUSTERING PURPOSES
$MainNode=[System.Net.Dns]::GetHostName()
if($initiator_hostname[0] -eq $MainNode)
{
	# GETTING THE IP ADDRESSES OF EACH NODE INTO A VARIABLE "IPAdd". THIS VARIABLE IS USED FOR CONNECTING THE ISCSI INITIATOR WITH THE ISCSI TARGET
	for ($i=0; $i -lt $initiator_ip.length -1; $i++) {
		$IPAdd += "IPAddress:" + $initiator_ip[$i] + ",";
	}
	$IPAdd += "IPAddress:" + $initiator_ip[$i]

	Add-Content "c:\disk.ps1" "Import-Module MicrosoftIscsiTarget"
	# CREATE A NEW iSCSI TARGET NAMED "$TargetName" INTO THE iSCSI SERVER "$iSCSI_Hostname"
	Add-Content "c:\disk.ps1" "New-IscsiServerTarget -TargetName `"$TargetName`" -InitiatorId $IPAdd -ComputerName $iSCSI_Hostname"
	# ASSIGN THE DISKS TO THE ISCSI TARGET
	# DISK 1 WILL BE USED FOR FAILOVER CLUSTER DISK
	# DISK 2 WILL BE USED FOR MSDTC SERVICE CREATED ON THE CLUSTER
	# DISK 3 WILL USED FOR THE SQL FAILOVER CLUSTER DISK
	Add-Content "c:\disk.ps1" "Add-VirtualDiskTargetMapping -TargetName `"$TargetName`" -DevicePath `"c:\disks\disk1.vhd`" -ComputerName $iSCSI_Hostname"
	Add-Content "c:\disk.ps1" "Add-VirtualDiskTargetMapping -TargetName `"$TargetName`" -DevicePath `"c:\disks\disk2.vhd`" -ComputerName $iSCSI_Hostname"
	Add-Content "c:\disk.ps1" "Add-VirtualDiskTargetMapping -TargetName `"$TargetName`" -DevicePath `"c:\disks\disk3.vhd`" -ComputerName $iSCSI_Hostname"

	$domainPASS = convertto-securestring "$domain_password" -asplaintext force
	$domainCredential = New-Object -TypeName System.Management.Automation.PSCredential -ArgumentList "$domain_name\$domain_username",$domainPASS
	echo "CREATING ISCSI TARGET ON ISCSI SERVER AND ASSIGNING THE DISK TO IT..."
	# THIS COMMAND EXECUTES ON THE ISCSI SERVER WITH THE DOMAIN CREDENTIAL TO CREATE AN ISCSI TARGET.
	$error.clear()
	invoke-command -computername $iSCSI_Hostname -filepath C:\disk.ps1 -Credential $domainCredential
	check_error "ERROR:WHILE CREATING iSCSI TARGET"
	echo "iSCSI TARGET CREATED SUCCESSFULLY"
}

# AFTER CREATING THE ISCSI TARGET, CONNECT THE ISCSI INITIATOR OF EACH NODE INCLUDING THE FIRST NODE TO THE ISCSI TARGET
echo "CONFIGURING ISCSI INITIATOR..."
# ENABLE THE ISCSI INITIATOR START UP TO "Automatic"
$error.clear()
Set-Service -Name msiscsi -StartupType Automatic
check_error "ERROR:WHILE ENABLING THE ISCSI INITIATOR"
# START THE MICROSOFT ISCSI SERVICE
$error.clear()
Start-Service msiscsi
check_error "ERROR:WHILE STARTING iSCSI SERVICE"
# CHECK WHETHER ISCSI TARGET EXISTS OR NOT
[ScriptBlock] $global:Check_iSCSITarget_ScriptBlock = {

	param($TargetName, $iSCSI_Hostname)
	import-module MicrosoftIscsiTarget
	[string]$global:target=Get-IscsiServerTarget -TargetName $TargetName -ComputerName $iSCSI_Hostname | Select-Object TargetName | Select-String "TargetName"
	return $target
}
$wait_interval = 30
$wait_limit = 300
for ($wait_c=0; $wait_c -le $wait_limit; ) {

	$domainPASS = convertto-securestring "$domain_password" -asplaintext force
	$domainCredential = New-Object -TypeName System.Management.Automation.PSCredential -ArgumentList "$domain_name\$domain_username",$domainPASS
	$error.clear()
	invoke-command -computername $iSCSI_Hostname -scriptblock $Check_iSCSITarget_ScriptBlock -argumentlist $TargetName, $iSCSI_Hostname -Credential $domainCredential
	if ($error.count -eq 0)
	{
		echo "iSCSI TARGET EXISTS"
		# FINAL WAIT FOR THINGS TO SETTLE DOWN
		Start-Sleep $wait_interval
		break;
	}
	else
	{
		echo "WAITING FOR ISCSI TARGET CREATION TO COMPLETE..."
		Start-Sleep $wait_interval
		$wait_c = $wait_c + $wait_interval
		if ($wait_c -ge $wait_limit) {
			echo "Abort. ISCSI TARGET CREATION DID NOT FINISH WITHIN $wait_limit seconds."
			exit 1
		}
	}
}
# ADD ISCSI TARGET
$error.clear()
iscsicli.exe QAddTargetPortal $iSCSI_Target_IP
check_error "ERROR:WHILE ADDING iSCSI TARGET"
# LOGIN TO TARGET
$error.clear()
iscsicli.exe QLoginTarget "iqn.1991-05.com.microsoft:$iSCSI_Hostname-$TargetName-target"
check_error "ERROR:WHILE LOGIN TO THE iSCSI TARGET"
# SET UP A PERSISTENT CONNECTION (RECONNECTS AFTER REBOOT SERVER)
$error.clear()
iscsicli.exe PersistentLoginTarget "iqn.1991-05.com.microsoft:$iSCSI_Hostname-$TargetName-target" T * * * * * * * * * * * * * * * 0
check_error "ERROR:WHILE SETTING UP PERSISTENT CONNECTION"
echo "iSCSI INITIATOR CONFIGURED SUCCESSFULLY"

