ns8:component-version-script.script:
#!/bin/bash # Import global conf . $global_conf
export PATH=$PATH:/usr/local/sbin:/usr/local/bin:/usr/sbin:/usr/bin:/sbin:/bin set -e
service httpd restart

######################################################################################################################
# PURPOSE: 1. Disable firewall setting for node communication.                                                       #
######################################################################################################################
# INTERNAL FUNCTIONS -- START
Function check_error
{
	if ($error.count -gt 0)
	{ 
		error_exit $args[0] 
	}
}

Function error_exit
{
	echo $args[0]
	exit 1
}
# INTERNAL FUNCTIONS -- END

# DISABLE FIREWALL
echo "DISABLING FIREWALL FOR SERVERS COMMUNICATION"
netsh advfirewall set allprofiles state off
check_error "ERROR:WHILE DIABLING FIREWALL SETTING"

supported-os-details.exec-script:
$error.clear()
# Auto convert input properties to local Powershell variables

# Parsing JSON elements into Powershell object
function ParseJsonProps ($jsonFile)
{
 $jsonjsonCharCount = 0
 $newObjStart = $false
 $arrayStart = $false
 $tokenStart = $false
 $valueStart = $false
 $escapeStart = $false
 
 $jsonFileLen = $jsonFile.length
 $jsonObj = New-Object System.Text.StringBuilder
 
 ForEach($c in $jsonFile.ToCharArray()) {
    $jsonCharCount++
    $tmpStr = ""
    switch -regex ($c) {
        "{" { 
            if($newObjStart -eq $false) {
                $tmpStr = "(New-Object PSObject "
                $newObjStart = $true
            } else {
                $tmpStr = $c
            }
        }
        "}" {
            if($jsonCharCount -lt $jsonFileLen) {
                $tmpStr = $c
            } else {
                $tmpStr = ")"
            }
        }
        '"' {
            if($tokenStart -eq $false -and $valueStart -eq $false -and $arrayStart -eq $false) {
                $tmpStr = '| Add-Member -Passthru NoteProperty "'
            } else {
                if($escapeStart -eq $false) {
                    $tmpStr = $c
                } else {
                    $tmpStr = "```""
                    $escapeStart = $false
                }
            }
            $tokenStart = $true
        }
        ":" {
            if($valueStart -eq $true -or $arrayStart -eq $true) {
                $tmpStr = $c
            } else {
                $tmpStr = " "
            }
            $valueStart = $true
        }
        "," {
            if($arrayStart) {
                $tmpStr = $c
            } else {
                $valueStart = $false
                $tokenStart = $false
            }
        }	
        "\[" {
            $tmpStr = "@("
            $arrayStart = $true
        }
        "\]" {
            $tmpStr = ")"
            $arrayStart = $false
        }
        "``" {
            $tmpStr = "````"
        }
        "\\" {
            if($escapeStart -eq $false) {
                $escapeStart = $true
            } else {
                $tmpStr = $c
                $escapeStart = $false
            }
        }
        "\$" {
            if($escapeStart -eq $false) {
                $tmpStr = '`$'
            } else {
                $tmpStr = '`$'
                $escapeStart = $false
            }
        }
        "n" {
            if($escapeStart -eq $false) {
                $tmpStr = $c
            } else {
                $tmpStr = "``n"
                $escapeStart = $false
            }
        }
        "r" {
            if($escapeStart -eq $false) {
                $tmpStr = $c
            } else {
                $tmpStr = "``r"
                $escapeStart = $false
            }
        }
        "t" {
            if($escapeStart -eq $false) {
                $tmpStr = $c
            } else {
                $tmpStr = "``t"
                $escapeStart = $false
            }
        }
        "b" {
            if($escapeStart -eq $false) {
                $tmpStr = $c
            } else {
                $tmpStr = "``b"
                $escapeStart = $false
            }
        }
        default {
            if($escapeStart -eq $false) {
                $tmpStr = $c
            } else {
                $tmpStr = "\$c"
                $escapeStart = $false
            }
            #$tmpStr = $c
            #if($escapeStart -eq $true) {
            #    $escapeStart = $false
            #}
        }
    }                        
    [void] $jsonObj.Append($tmpStr)
 }
 
 $jsonObj | Invoke-Expression
}

$vmw_appd_json_file = Get-Content "$($Args[1])"
$vmw_appd_json_obj = ParseJsonProps $vmw_appd_json_file
$vmw_appd_json_props = $vmw_appd_json_obj.PsObject.Properties

# Assigning obj to local variable
ForEach ($vmw_appd_obj_prop in $vmw_appd_json_props)
{
 $vmw_appd_obj_prop_name = $vmw_appd_obj_prop.Name;
 #$vmw_appd_obj_prop_name_full = '$vmw_appd_json_obj' + '.' + $vmw_appd_obj_prop_name
 #$vmw_appd_obj_prop_value = Invoke-Expression $vmw_appd_obj_prop_name_full
 Set-Variable -Name $vmw_appd_obj_prop_name -value $vmw_appd_json_obj."$vmw_appd_obj_prop_name"
}


# Execute user's Powershell script
$error.clear()
$global:LASTEXITCODE = $null
. "$($Args[0])" 2>&1 | out-file -encoding Default "$($Args[3])"

if ($LASTEXITCODE -and $LASTEXITCODE -ne 0) {
 "`nABORT. LASTEXITCODE is non-zero.`n" | out-file -encoding Default -append $($Args[3])
 exit $LASTEXITCODE
}

if ($error.count -gt 0) {
 "`nABORT. Encountered error in Powershell.`n" | out-file -encoding Default -append $($Args[3])
 exit $error.count
 }

# Export Powershell variables to output properties file
ForEach ($vmw_appd_obj_prop in $vmw_appd_json_props)
{
 $vmw_appd_obj_prop_name = $vmw_appd_obj_prop.Name;
 $vmw_appd_obj_prop_type = (($vmw_appd_json_obj.($vmw_appd_obj_prop_name)).GetType()).Name

 # Export only String AppD's properties; Output Array properties is not currently supported.
 if ($vmw_appd_obj_prop_type.Contains("String")) {
      $vmw_appd_obj_prop_name_expanded = '$' + $vmw_appd_obj_prop_name

      # The ExpandString takes care of three cases. 
      # Case-I when the string is set via input properties
      # Case-II when the string is set via input properties and overridden in script (install/configure..)
      # Case-III lastly when it is only set via script (install/configure..) and left empty in input props
      #
      # Also note that powershell as is does not care if this variable was a computed 
      # or a string variable that filter happens at a different layer.
      #
      $expandedUserVariable = $ExecutionContext.InvokeCommand.ExpandString($vmw_appd_obj_prop_name_expanded)

     ($vmw_appd_obj_prop_name + '=' + $expandedUserVariable) | Add-Content $($Args[2]) 
 }
}

ns8:component-version-script.script:
###################################################################################################################### # PURPOSE: This script is used to install weblogic application server 12c on windows operationg system. # # NOTE: This script requires the following variables before installing WebLogic Server as a Windows Service: # # bea_home - The root directory of your Middleware. Change it as per your choice. # # wl_home - The root directory of your WebLogic installation. Change it as per your choice. # # weblogic_installer - URL of exe installer of weblogic server. Change it as per your installer path. # # e.g. http://your_httpd_serverIP/weblogic/wls12c.exe. # # java_home - Path for your system jdk/jre. Change it as per your jdk/jre path. e.g c:\java\jdk6 # ######################################################################################################################
# VALIDATION FUNCTIONS -- START Function check_error { if ($error.count -gt 0) { error_exit $args[0] } }
Function error_exit { echo $args[0] exit 1 } # VALIDATION FUNCTIONS -- END
# PARAMETER VALIDATION -- START echo "PARAMETER VALIDATION -- START" if (($bea_home -eq '')) { error_exit "ERROR:WEBLOGIC INSTALLATION ROOT PATH NOT SET" }
if (($wl_home -eq '')) { error_exit "ERROR:WEBLOGIC APPLICATION SERVER INSTALLATION PATH NOT SET" }
if (($wl_tmp_files_dir -eq '')) { error_exit "ERROR:LOACTION FOR TEMPORARY FILES NOT SET" }
if (($weblogic_installer -eq '')) { error_exit "ERROR:WEBLOGIC INSTALLER URL NOT SET" }
if (($java_home -eq '')) { error_exit "ERROR:JAVA_HOME ENVIRONMENT VARIABLE NOT SET" } echo "PARAMETERS VALIDATION -- DONE" # PARAMETER VALIDATION -- END
# PROPERTIES # VARIABLE FOR XML REQUIRED TO INSTALL WEBLOGIC SERVER $WL_CONFIG="$bea_home\weblogic_silentInstall.xml" # JAVA_HOME ENVIRONMENT VARIABLE $env:JAVA_HOME = "$java_home" # PATH ENVIRONMENT VARIABLE $env:PATH += ";$env:JAVA_HOME\bin"
# CREATE INSTALLATION DIRECTORY FOR WEBLOGIC SERVER if (!(Test-Path -Path $wl_home)) { echo "CREATING WEBLOGIC INSTALLATION DIRECTORY..." New-Item $wl_home -type directory echo "CREATING DIRECTORY FOR HOLDING TEMPORARY FILES..." New-Item $wl_tmp_files_dir -type directory }
# DOWNLOAD WEBLOGIC 12C INSTALLER echo "DOWNLOADING WEBLOGIC INSTALLER..." $WEBCLIENT=new-object system.net.webclient $WEBCLIENT.Downloadfile($weblogic_installer,"$wl_tmp_files_dir\WebLogic12c.exe") check_error "ERROR:WHILE DOWNLOADING WEBLOGIC INSTALLER" echo "WEBLOGIC INSTALLER DOWNLOADED SUCCESSFULLY"
# CREATE SILENT XML FILE TO INSTALL WEBLOGIC SERVER SILENTLY echo "CREATING $WL_CONFIG FOR SILENT INSTALLATION OF WEBLOGIC SERVER..." Add-Content "$WL_CONFIG" '<?xml version="1.0" encoding="UTF-8"?>' Add-Content "$WL_CONFIG" '<domain-template-descriptor>' Add-Content "$WL_CONFIG" '<input-fields>' Add-Content "$WL_CONFIG" " <data-value name=`"BEAHOME`" value=`"$bea_home`" />" Add-Content "$WL_CONFIG" " <data-value name=`"WLS_INSTALL_DIR`" value=`"$wl_home`" />" Add-Content "$WL_CONFIG" " <data-value name=`"WLW_INSTALL_DIR`" value=`"$wl_home`" />" Add-Content "$WL_CONFIG" " <data-value name=`"COMPONENT_PATHS`" value=`"WebLogic Server|Oracle Coherence`" />" Add-Content "$WL_CONFIG" "</input-fields>" Add-Content "$WL_CONFIG" "</domain-template-descriptor>" echo "$WL_CONFIG CREATED"
# COMMAND LINE ARGUMENTS TO INSTALL WEBLOGIC SERVER $WEBLOGIC_EXE = '.\WebLogic12c.exe ' $WEBLOGIC_PRM = @() $WEBLOGIC_PRM += "-mode=silent" $WEBLOGIC_PRM += "-silent_xml=$WL_CONFIG" $WEBLOGIC_PRM += "-log=$bea_home\weblogicSilentInstallation.log" echo "INSTALLING WEBLOGIC SERVER..." # MOVE TO THE DIRECTORY WHICH CONTAINS THE WEBLOGIC INSTALLATION FILE. cd $wl_tmp_files_dir # FINALLY RUN THE INSTALLER TO INSTALL THE WEBLOGIC APPLICATION SERVER cmd /c $WEBLOGIC_EXE $WEBLOGIC_PRM
# SUCCESSFUL INSTALLTION CRITERIA FOR WEBLOGIC # IF WEBLOGIC SERVER IS INSTALLED SUCCESSFULLY THEN ITS LOG CONTAINS THE STRING "The installation was successfull". $SEARCH_STRING = "The installation was successfull!" $SEARCH_RESULT = select-string -pattern $SEARCH_STRING -path $bea_home\weblogicSilentInstallation.log If ($SEARCH_RESULT -eq $null) { echo "ERROR:WHILE INSTALLING WEBLOGIC SERVER" exit 1 } else { echo "WEBLOGIC SERVER INSTALLED SUCCESSFULLY" echo "REMOVING SAMPLE DOMAINS TO AVOID THEM BEING PICKED UP..." echo "EXECING Remove-Item -Force -Recurse $wl_home\samples\domains\wl_server..." Remove-Item -Force -Recurse $wl_home\samples\domains\wl_server echo "EXECING Remove-Item -Force -Recurse $wl_home\samples\domains\medrec-spring..." Remove-Item -Force -Recurse $wl_home\samples\domains\medrec-spring echo "EXECING Remove-Item -Force -Recurse $wl_home\samples\domains\medrec..." Remove-Item -Force -Recurse $wl_home\samples\domains\medrec echo "REMOVING WEBLOGIC INSTALLER FILE TO SAVE DISK SPACE..." Remove-Item -Force -Recurse "$wl_tmp_files_dir\WebLogic12c.exe" }

supported-os-details.exec-script:
$error.clear()
