ns8:component-version-script.script:
######################################################################################################################
# PURPOSE: 1. This script is used to create a domain and a cluster on the weblogic application server 12c            #
#          2. This script is used to configure the node manager of the admin server                                  #
######################################################################################################################

# VALIDATION FUNCTIONS -- START
Function check_error
{
	if ($error.count -gt 0)
	{ 
		error_exit $args[0] 
	}
}
Function error_exit
{
	echo $args[0]
	exit 1
}
Function valid_string
{ 
	$STR = $args[0]
    $ValidSTR = $true
    $ValidSTR = ($STR -match "^[A-Za-z]{1,}[A-Za-z0-9_-]{1,}$")
    if(-not $ValidSTR){return $false}
	return $ValidSTR
}
Function valid_password
{ 
	$STR = $args[0]
    $ValidSTR = $true
    $ValidSTR = ($STR -match "^[A-Za-z]{1,}[0-9_@$%^#+=]{0,}[A-Za-z0-9]{0,}$")
    if(-not $ValidSTR){return $false}
	return $ValidSTR
}
Function valid_int
{ 
	$STR = $args[0]
    $ValidSTR = $true
    $ValidSTR = ($STR -match "^[0-9]{1,9}$")
    if(-not $ValidSTR){return $false}
	return $ValidSTR
}
Function valid_ip
{
	$IP = $args[0]
    $ValidIP = $true

    $ValidIP = ($IP -match "^[1-9]\d{0,2}\.\d{1,3}\.\d{1,3}\.\d{1,3}$") 
    if(-not $ValidIP){return $false}

    $myip = $IP -split "\." #Each octet is element of an array
    $ValidIP = ([int]$myip[0] -gt 0 -and [int]$myip[0] -le 255) #First octet is between 1-255
    if(-not $ValidIP){return $false}

    for($i = 1;$i -le 3;$i++){
        $ValidIP = ([int]$myip[$i] -ge 0 -and [int]$myip[$i] -le 255) #Remain octets between 0-255
        if(-not $ValidIP){return $false}
    }
    return $ValidIP #$ValidIP will always be true at this point.
}
# VALIDATION FUNCTIONS -- END

