# FUNCTION TO CONFIGURE WEBLOGIC ADMIN SERVER
Function Configure-AS
{
    param([string] $CLASSPATH, [string] $filePath) 
    start-process "cmd.exe" "/c java.exe -classpath $CLASSPATH weblogic.WLST $filePath" -Wait
}
