$WAIT_LIMIT = 1800
for ($WAIT_C=0; $WAIT_C -le $WAIT_LIMIT; ) {

    echo "WAITING FOR WEBLOGIC DOMAIN/CLUSTER CREATION TO COMPLETE..."
    if (Test-Path $WL_DOMAIN_LOG_LOCATION) {
        # Final wait for things to settle down
        Start-Sleep $WAIT_INTERVAL
		echo "WEBLOGIC DOMAIN/CLUSTER CREATED SUCCESSFULLY!"
        break
    }

    if ($WAIT_C -ge $WAIT_LIMIT) {
        error_exit "ABORT. WEBLOGIC DOMAIN/CLUSTER CREATION DID NOT FINISH WITHIN $WAIT_LIMIT SECONDS."
    }
    $WAIT_C = $WAIT_C + $WAIT_INTERVAL
    Start-Sleep $WAIT_INTERVAL
}

