######################################################################################################################
# PURPOSE: This script is used to pack the weblogic domain                                                           #
# Pack of the domain means collecting the domain congurations into a jar file                                        #
# This jar file will be copied to each managed node that are the part of the weblogic domain                         #
######################################################################################################################

# VALIDATION FUNCTIONS -- START
Function check_error
{
	if ($error.count -gt 0)
	{ 
		error_exit $args[0] 
	}
}
Function error_exit
{
	echo $args[0]
	exit 1
}
Function valid_string
{ 
	$STR = $args[0]
    $ValidSTR = $true
    $ValidSTR = ($STR -match "^[A-Za-z]{1,}[A-Za-z0-9_-]{1,}$")
    if(-not $ValidSTR){return $false}
	return $ValidSTR
}
# VALIDATION FUNCTIONS -- END

# PARAMETER VALIDATION -- START
echo "PARAMETER VALIDATION -- START"
if (($bea_home -eq ''))
{
	error_exit "ERROR:WEBLOGIC INSTALLATION ROOT PATH NOT SET"	
}
if (($wl_home -eq ''))
{
	error_exit "ERROR:WEBLOGIC APPLICATION SERVER INSTALLATION PATH NOT SET"	
}
if (($wl_tmp_files_dir -eq ''))
{
	error_exit "ERROR:LOACTION FOR TEMPORARY FILES NOT SET"	
}
if (($wl_domain -eq ''))
{
	error_exit "ERROR:WEBLOGIC DOMAIN NAME NOT SET"	
}
else
{
	if(!(valid_string $wl_domain))
	{ error_exit "$wl_domain IS NOT A VALID STRING"}
}
echo "PARAMETERS VALIDATION -- DONE"
# PARAMETER VALIDATION -- END

# INTERNAL PROPERTIES -- START
# JAVA_HOME ENVIRONMENT VARIABLE
$env:JAVA_HOME="$bea_home\jdk160_29"
# PATH ENVIRONMENT VARIABLE
$env:PATH +=";$env:JAVA_HOME\bin"
$WL_DOMAIN_PATH="$wl_home\user_projects\domains\$wl_domain"
$WL_SERVERS_PATH="$WL_DOMAIN_PATH\servers"
# FULL DIRECTORY PATH WHERE THE DOMAIN WILL BE PACKED INTO a JAR FILE
$PACKED_DOMAINS_DIR="$bea_home\packed_domains"
# NAME OF THE JAR FILE
$TEMPLATE_FILE="$PACKED_DOMAINS_DIR\${wl_domain}.jar"
# CLASSPATH ENVIRONMENT VARIABLE
$env:CLASSPATH=. "$wl_home\server\bin\setWLSEnv.cmd" | Select-String "CLASSPATH"
# INTERNAL PROPERTIES -- END

# SCRIPT EXECUTION -- START
echo "SET ENVIRONMENT VARIABLE FOR WEBLOGIC"
. "$wl_home\server\bin\setWLSEnv.cmd"

echo "Packing domain $wl_domain"
Add-Content "$wl_tmp_files_dir\pack.cmd" "    IF NOT EXIST $PACKED_DOMAINS_DIR ( mkdir $PACKED_DOMAINS_DIR )"
Add-Content "$wl_tmp_files_dir\pack.cmd" "    IF EXIST $TEMPLATE_FILE ( DEL /f /q $TEMPLATE_FILE )"
Add-Content "$wl_tmp_files_dir\pack.cmd" "    $wl_home\common\bin\pack.cmd -managed=true -domain=$WL_DOMAIN_PATH -template=$TEMPLATE_FILE -template_name=$wl_domain -log_priority=debug -log=pack.log"

. "$wl_tmp_files_dir\pack.cmd"
$WAIT_INTERVAL = 20
$WAIT_LIMIT = 600
for ($WAIT_C=0; $WAIT_C -le $WAIT_LIMIT; ) {

    echo "WAITING FOR DOMAIN PACKING TO COMPLETE..."
    if (Test-Path $TEMPLATE_FILE) {
        # Final wait for things to settle down
        Start-Sleep $WAIT_INTERVAL
		echo "WEBLOGIC DOMAIN PACKED SUCCESSFULLY TO $TEMPLATE_FILE"
        break
    }

    if ($WAIT_C -ge $WAIT_LIMIT) {
        error_exit "ABORT. DOMAIN PACKING DID NOT FINISH WITHIN $WAIT_LIMIT SECONDS."
    }

    $WAIT_C = $WAIT_C + $WAIT_INTERVAL
    Start-Sleep $WAIT_INTERVAL
}

echo "MAKING THE FOLDER $PACKED_DOMAINS_DIR SHAREABLE"
$Shares=[WMICLASS]"WIN32_Share"
$Shares.Create("$PACKED_DOMAINS_DIR","packed_domains",0)
# SCRIPT EXECUTION -- END

supported-os-details.exec-script:
$error.clear()
# Auto convert input properties to local Powershell variables

# Parsing JSON elements into Powershell object
function ParseJsonProps ($jsonFile)
{
 $jsonjsonCharCount = 0
 $newObjStart = $false
 $arrayStart = $false
 $tokenStart = $false
 $valueStart = $false
 $escapeStart = $false
 
 $jsonFileLen = $jsonFile.length
 $jsonObj = New-Object System.Text.StringBuilder
 
 ForEach($c in $jsonFile.ToCharArray()) {
    $jsonCharCount++
    $tmpStr = ""
    switch -regex ($c) {
        "{" { 
            if($newObjStart -eq $false) {
                $tmpStr = "(New-Object PSObject "
                $newObjStart = $true
            } else {
                $tmpStr = $c
            }
        }
        "}" {
            if($jsonCharCount -lt $jsonFileLen) {
                $tmpStr = $c
            } else {
                $tmpStr = ")"
            }
        }
        '"' {
            if($tokenStart -eq $false -and $valueStart -eq $false -and $arrayStart -eq $false) {
                $tmpStr = '| Add-Member -Passthru NoteProperty "'
            } else {
                if($escapeStart -eq $false) {
                    $tmpStr = $c
                } else {
                    $tmpStr = "```""
                    $escapeStart = $false
                }
            }
            $tokenStart = $true
        }
        ":" {
            if($valueStart -eq $true -or $arrayStart -eq $true) {
                $tmpStr = $c
            } else {
                $tmpStr = " "
            }
            $valueStart = $true
        }
        "," {
            if($arrayStart) {
                $tmpStr = $c
            } else {
                $valueStart = $false
                $tokenStart = $false
            }
        }	
        "\[" {
            $tmpStr = "@("
            $arrayStart = $true
        }
        "\]" {
            $tmpStr = ")"
            $arrayStart = $false
        }
        "``" {
            $tmpStr = "````"
        }
        "\\" {
            if($escapeStart -eq $false) {
                $escapeStart = $true
            } else {
                $tmpStr = $c
                $escapeStart = $false
            }
        }
        "\$" {
            if($escapeStart -eq $false) {
                $tmpStr = '`$'
            } else {
                $tmpStr = '`$'
                $escapeStart = $false
            }
        }
        "n" {
            if($escapeStart -eq $false) {
                $tmpStr = $c
            } else {
                $tmpStr = "``n"
                $escapeStart = $false
            }
        }
        "r" {
            if($escapeStart -eq $false) {
                $tmpStr = $c
            } else {
                $tmpStr = "``r"
                $escapeStart = $false
            }
        }
        "t" {
            if($escapeStart -eq $false) {
                $tmpStr = $c
            } else {
                $tmpStr = "``t"
                $escapeStart = $false
            }
        }
        "b" {
            if($escapeStart -eq $false) {
                $tmpStr = $c
            } else {
                $tmpStr = "``b"
                $escapeStart = $false
            }
        }
        default {
            if($escapeStart -eq $false) {
                $tmpStr = $c
            } else {
                $tmpStr = "\$c"
                $escapeStart = $false
            }
            #$tmpStr = $c
            #if($escapeStart -eq $true) {
            #    $escapeStart = $false
            #}
        }
    }                        
    [void] $jsonObj.Append($tmpStr)
 }
 
 $jsonObj | Invoke-Expression
}

$vmw_appd_json_file = Get-Content "$($Args[1])"
$vmw_appd_json_obj = ParseJsonProps $vmw_appd_json_file
$vmw_appd_json_props = $vmw_appd_json_obj.PsObject.Properties

# Assigning obj to local variable
ForEach ($vmw_appd_obj_prop in $vmw_appd_json_props)
{
 $vmw_appd_obj_prop_name = $vmw_appd_obj_prop.Name;
 #$vmw_appd_obj_prop_name_full = '$vmw_appd_json_obj' + '.' + $vmw_appd_obj_prop_name
 #$vmw_appd_obj_prop_value = Invoke-Expression $vmw_appd_obj_prop_name_full
 Set-Variable -Name $vmw_appd_obj_prop_name -value $vmw_appd_json_obj."$vmw_appd_obj_prop_name"
}


# Execute user's Powershell script
$error.clear()
$global:LASTEXITCODE = $null
. "$($Args[0])" 2>&1 | out-file -encoding Default "$($Args[3])"

if ($LASTEXITCODE -and $LASTEXITCODE -ne 0) {
 "`nABORT. LASTEXITCODE is non-zero.`n" | out-file -encoding Default -append $($Args[3])
 exit $LASTEXITCODE
}

if ($error.count -gt 0) {
 "`nABORT. Encountered error in Powershell.`n" | out-file -encoding Default -append $($Args[3])
 exit $error.count
 }

# Export Powershell variables to output properties file
ForEach ($vmw_appd_obj_prop in $vmw_appd_json_props)
{
 $vmw_appd_obj_prop_name = $vmw_appd_obj_prop.Name;
 $vmw_appd_obj_prop_type = (($vmw_appd_json_obj.($vmw_appd_obj_prop_name)).GetType()).Name

 # Export only String AppD's properties; Output Array properties is not currently supported.
 if ($vmw_appd_obj_prop_type.Contains("String")) {
      $vmw_appd_obj_prop_name_expanded = '$' + $vmw_appd_obj_prop_name

      # The ExpandString takes care of three cases. 
      # Case-I when the string is set via input properties
      # Case-II when the string is set via input properties and overridden in script (install/configure..)
      # Case-III lastly when it is only set via script (install/configure..) and left empty in input props
      #
      # Also note that powershell as is does not care if this variable was a computed 
      # or a string variable that filter happens at a different layer.
      #
      $expandedUserVariable = $ExecutionContext.InvokeCommand.ExpandString($vmw_appd_obj_prop_name_expanded)

     ($vmw_appd_obj_prop_name + '=' + $expandedUserVariable) | Add-Content $($Args[2]) 
 }
}

