Add-Content "$wl_tmp_files_dir/setupSampleAppCommon.py" "    edit()"
Add-Content "$wl_tmp_files_dir/setupSampleAppCommon.py" "    startEdit(exclusive=`"true`", waitTimeInMillis=36000)"
Add-Content "$wl_tmp_files_dir/setupSampleAppCommon.py" "    return"
