Add-Content "$wl_tmp_files_dir/setupSampleAppConfig.py" "    dataSourceParams.setGlobalTransactionsProtocol(`"$db_proto`")"
Add-Content "$wl_tmp_files_dir/setupSampleAppConfig.py" "    driverParams = jdbcResource.getJDBCDriverParams()"
Add-Content "$wl_tmp_files_dir/setupSampleAppConfig.py" "    driverParams.setUrl(`"$DB_URL`")"
Add-Content "$wl_tmp_files_dir/setupSampleAppConfig.py" "    driverParams.setDriverName(`"$db_driver`")"
Add-Content "$wl_tmp_files_dir/setupSampleAppConfig.py" "    driverParams.setPassword(`"$db_password`")"
Add-Content "$wl_tmp_files_dir/setupSampleAppConfig.py" "    driverProperties = driverParams.getProperties()"
Add-Content "$wl_tmp_files_dir/setupSampleAppConfig.py" "    driverProperties.createProperty(`"user`")"
Add-Content "$wl_tmp_files_dir/setupSampleAppConfig.py" "    userProperty = driverProperties.lookupProperty(`"user`")"
Add-Content "$wl_tmp_files_dir/setupSampleAppConfig.py" "    userProperty.setValue(`"$db_username`")"
Add-Content "$wl_tmp_files_dir/setupSampleAppConfig.py" "    driverProperties.createProperty(`"databaseName`")"
Add-Content "$wl_tmp_files_dir/setupSampleAppConfig.py" "    dbNameProperty = driverProperties.lookupProperty(`"databaseName`")"
Add-Content "$wl_tmp_files_dir/setupSampleAppConfig.py" "    dbNameProperty.setValue(`"$db_name`")"
Add-Content "$wl_tmp_files_dir/setupSampleAppConfig.py" "    connectionPoolParams = jdbcResource.getJDBCConnectionPoolParams()"
Add-Content "$wl_tmp_files_dir/setupSampleAppConfig.py" "    connectionPoolParams.setTestTableName(`"$db_test_table`")"
Add-Content "$wl_tmp_files_dir/setupSampleAppConfig.py" "    connectionPoolParams.setConnectionCreationRetryFrequencySeconds(100)"
Add-Content "$wl_tmp_files_dir/setupSampleAppConfig.py" ""
Add-Content "$wl_tmp_files_dir/setupSampleAppConfig.py" "    saveAndActivate()"
Add-Content "$wl_tmp_files_dir/setupSampleAppConfig.py" "except:"
Add-Content "$wl_tmp_files_dir/setupSampleAppConfig.py" "    apply(traceback.print_exception, sys.exc_info())"
Add-Content "$wl_tmp_files_dir/setupSampleAppConfig.py" "    dumpStack()"
Add-Content "$wl_tmp_files_dir/setupSampleAppConfig.py" "    exit(exitcode=-1)"
Add-Content "$wl_tmp_files_dir/setupSampleAppConfig.py" ""
Add-Content "$wl_tmp_files_dir/setupSampleAppConfig.py" "exit()"

