# WHENEVER WE TRY TO START ADMIN SERVER WITHOUT FIXING THE BUG 13658083 WE GET THE FOLLOWING ERROR
# 1) locate the bundled Java EE 6 endorsed directory in $WL_HOME/endorsed.
# 2) copy those JAR files to $JAVA_HOME/jre/lib/endorsed OR add the endorsed directory to the value specified by system property java.endorsed.dirs
echo "FIXING WEBLOGIC BUG 13658083..."
if (!(Test-Path -Path $env:JAVA_HOME\jre\lib\endorsed))
{
    New-Item $env:JAVA_HOME\jre\lib\endorsed -type directory
}
cp -Force $wl_home\endorsed\* $env:JAVA_HOME\jre\lib\endorsed
echo "WEBLOGIC BUG 13658083 FIXED SUCCESSFULLY!"

# GENERATE config.py WITH SOME COMMON STUFF
Add-Content "$wl_tmp_files_dir\installMSconfigCommon.py" "from java.util import *"
Add-Content "$wl_tmp_files_dir\installMSconfigCommon.py" "from javax.management import *"
Add-Content "$wl_tmp_files_dir\installMSconfigCommon.py" "import sys, traceback"
Add-Content "$wl_tmp_files_dir\installMSconfigCommon.py" ""
$temp_path=$WL_DOMAIN_PATH.Replace('\','/')
Add-Content "$wl_tmp_files_dir\installMSconfigCommon.py" "theServerLog = `"$temp_path`" + `"/servers/$wl_admin_server_name/logs/AdminServer.log`""
Add-Content "$wl_tmp_files_dir\installMSconfigCommon.py" "adminServerURL = `"$ADMIN_SERVER_URL`""
Add-Content "$wl_tmp_files_dir\installMSconfigCommon.py" "adminHTTPURL = `"http://${wl_admin_server_addr}:${wl_admin_server_port}`""
Add-Content "$wl_tmp_files_dir\installMSconfigCommon.py" ""
Add-Content "$wl_tmp_files_dir\installMSconfigCommon.py" "def nodeManagerConnect():"
Add-Content "$wl_tmp_files_dir\installMSconfigCommon.py" "    nmConnect(`"$wl_node_manager_user`", `"$wl_node_manager_password`", `"$wl_node_manager_addr`", $wl_node_manager_port, `"$wl_domain`", `"$temp_path`", `"plain`", `"true`")"
Add-Content "$wl_tmp_files_dir\installMSconfigCommon.py" "    return"
Add-Content "$wl_tmp_files_dir\installMSconfigCommon.py" ""
Add-Content "$wl_tmp_files_dir\installMSconfigCommon.py" "def shutdownNodeManager():"
Add-Content "$wl_tmp_files_dir\installMSconfigCommon.py" "    stopNodeManager()"
Add-Content "$wl_tmp_files_dir\installMSconfigCommon.py" "    return"
Add-Content "$wl_tmp_files_dir\installMSconfigCommon.py" ""

# SET NODEMANAGER CONFIGURATION AND WRAPPER SCRIPT THAT IS CALLED BY NODEMANAGER WHEN THE MANAGEDSERVER IS TO BE STARTED
echo "GENERATING THE NODEMANAGER CONFIGURATION"
# SETUP NODE MANAGER
# THIS FILE CONTAINS ALL THE REQUIRED INFORMATIONS FOR NODEMANAGER
$NM_PROPS_FILE="$WL_NODEMANAGER_HOME\nodemanager.properties" 
$temp_path=$wl_home.Replace('\','/')
Add-Content "$NM_PROPS_FILE" "WeblogicHome=$temp_path"
$temp_path=$env:JAVA_HOME.Replace('\','/')
Add-Content "$NM_PROPS_FILE" "JavaHome=$temp_path/jre"
$temp_path=$WL_NODEMANAGER_HOME.Replace('\','/')
Add-Content "$NM_PROPS_FILE" "NodeManagerHome=$temp_path"
Add-Content "$NM_PROPS_FILE" "DomainsFile=$temp_path/nodemanager.domains"
Add-Content "$NM_PROPS_FILE" "ListenAddress=$wl_node_manager_addr"
Add-Content "$NM_PROPS_FILE" "ListenPort=$wl_node_manager_port"
Add-Content "$NM_PROPS_FILE" "LogFile=$temp_path/nodemanager.log"
Add-Content "$NM_PROPS_FILE" "LogLimit=0"
Add-Content "$NM_PROPS_FILE" "DomainsDirRemoteSharingEnabled=false"
Add-Content "$NM_PROPS_FILE" "AuthenticationEnabled=false"
Add-Content "$NM_PROPS_FILE" "LogLevel=INFO"
Add-Content "$NM_PROPS_FILE" "DomainsFileEnabled=true"
Add-Content "$NM_PROPS_FILE" "StartScriptName=service_migration/nmStartManagedServer.cmd # Not used, all info is defined in server setup "
Add-Content "$NM_PROPS_FILE" "NativeVersionEnabled=false"
Add-Content "$NM_PROPS_FILE" "LogToStderr=true"
Add-Content "$NM_PROPS_FILE" "SecureListener=false"
Add-Content "$NM_PROPS_FILE" "LogCount=1"
Add-Content "$NM_PROPS_FILE" "DomainRegistrationEnabled=false"
Add-Content "$NM_PROPS_FILE" "StartScriptEnabled=false"
Add-Content "$NM_PROPS_FILE" "StopScriptEnabled=false"
Add-Content "$NM_PROPS_FILE" "QuitEnabled=true"
Add-Content "$NM_PROPS_FILE" "LogAppend=true"
Add-Content "$NM_PROPS_FILE" "StateCheckInterval=500"
Add-Content "$NM_PROPS_FILE" "CrashRecoveryEnabled=false"
Add-Content "$NM_PROPS_FILE" "LogFormatter=weblogic.nodemanager.server.LogFormatter"
Add-Content "$NM_PROPS_FILE" "ListenBacklog=50"
#Add-Content "$NM_PROPS_FILE" "PropertiesVersion=10.3"

# ENROLL THIS MACHINE WITH THE ADMINSERVER
cp $wl_tmp_files_dir\installMSconfigCommon.py $wl_tmp_files_dir\installMSenroll.py
Add-Content "$wl_tmp_files_dir\installMSenroll.py" "try:"
Add-Content "$wl_tmp_files_dir\installMSenroll.py" "    connect(`"$wl_admin_user`", `"$wl_admin_password`", adminServerURL)"
$temp1_path=$WL_DOMAIN_PATH.Replace('\','/')
$temp2_path=$WL_NODEMANAGER_HOME.Replace('\','/')
Add-Content "$wl_tmp_files_dir\installMSenroll.py" "    nmEnroll(`"$temp1_path`" , `"$temp2_path`")"
Add-Content "$wl_tmp_files_dir\installMSenroll.py" "    disconnect()"
Add-Content "$wl_tmp_files_dir\installMSenroll.py" "except:"
Add-Content "$wl_tmp_files_dir\installMSenroll.py" "    apply(traceback.print_exception, sys.exc_info())"
Add-Content "$wl_tmp_files_dir\installMSenroll.py" "    dumpStack()"
Add-Content "$wl_tmp_files_dir\installMSenroll.py" "    exit(exitcode=-1)"
Add-Content "$wl_tmp_files_dir\installMSenroll.py" ""
Add-Content "$wl_tmp_files_dir\installMSenroll.py" "exit()"

echo "ENROLLING THE MACHINE..."
$error.clear()
Start-MS "$WL_JAR" "$wl_tmp_files_dir\installMSenroll.py"
check_error "ERROR:WHILE ENROLLING MANAGED SERVER"
echo "MANAGED SERVER ENROLLED SUCCESSFULLY!"

# AFTER ENROLLING IF WE ARE IN PRODUCTION MODE THE ADMINSERVER WILL GIVE US SOME RANDOM PROPERTIES, SET THEM BACK
echo "GENERATING THE NODEMANAGER startup.properties"
# Startup properties required for actually getting it going
#http://oraclemva.wordpress.com/2011/02/07/wls-nodemanager-and-startup-properties/
#http://docs.oracle.com/cd/E24329_01/web.1211/e21050/nodemgr_config.htm#BHCJHHDC
mkdir $WL_SERVERS_PATH\$wl_managed_server_name\data\nodemanager
$temp_path=$env:JAVA_HOME.Replace('\','/')
Add-Content "$WL_SERVERS_PATH\$wl_managed_server_name\data\nodemanager\startup.properties" "JavaHome=$temp_path"
$temp_path=$wl_home.Replace('\','/')
Add-Content "$WL_SERVERS_PATH\$wl_managed_server_name\data\nodemanager\startup.properties" "Arguments=$wl_managed_server_jvm_args -Djava.library.path=$temp_path/server/native/$wl_native_target"
#Add-Content "$WL_SERVERS_PATH\$wl_managed_server_name\data\nodemanager\startup.properties" "Arguments=$wl_managed_server_jvm_args "
Add-Content "$WL_SERVERS_PATH\$wl_managed_server_name\data\nodemanager\startup.properties" "SSLArguments=-Dweblogic.security.SSL.ignoreHostnameVerification=true -Dweblogic.ReverseDNSAllowed=false -Dweblogic.security.SSL.enforceConstraints=off"
Add-Content "$WL_SERVERS_PATH\$wl_managed_server_name\data\nodemanager\startup.properties" "RestartMax=2"
Add-Content "$WL_SERVERS_PATH\$wl_managed_server_name\data\nodemanager\startup.properties" "RestartDelaySeconds=0"
Add-Content "$WL_SERVERS_PATH\$wl_managed_server_name\data\nodemanager\startup.properties" "RestartInterval=3600"
Add-Content "$WL_SERVERS_PATH\$wl_managed_server_name\data\nodemanager\startup.properties" "AdminURL=$ADMIN_SERVER_URL"
Add-Content "$WL_SERVERS_PATH\$wl_managed_server_name\data\nodemanager\startup.properties" "AutoRestart=true"
Add-Content "$WL_SERVERS_PATH\$wl_managed_server_name\data\nodemanager\startup.properties" "AutoKillIfFailed=false"

# SET BOOT.PROPERTIES, IF PRODUCTION MODE LOOKS LIKE THIS IS NEEDED
echo "SETTING boot.properties"
Add-Content "$WL_SERVERS_PATH\$wl_managed_server_name\data\nodemanager\boot.properties" "username=$wl_admin_user"
Add-Content "$WL_SERVERS_PATH\$wl_managed_server_name\data\nodemanager\boot.properties" "password=$wl_admin_password"

# NM Password
if (Test-Path -Path "$WL_DOMAIN_PATH\config\nodemanager\nm_password.properties")
{
	remove-item -force "$WL_DOMAIN_PATH\config\nodemanager\nm_password.properties"
}
Add-Content "$WL_DOMAIN_PATH\config\nodemanager\nm_password.properties" "username=$wl_node_manager_user"
Add-Content "$WL_DOMAIN_PATH\config\nodemanager\nm_password.properties" "password=$wl_node_manager_password"

# IF WE DON'T DO THIS WE POSSIBLY RESULT IN ERROR
if (Test-Path -Path $WL_SERVERS_PATH\$wl_managed_server_name\data\ldap)
{
    echo "Removing $WL_SERVERS_PATH\$wl_managed_server_name\data\ldap"
	remove-item -force -recurse $WL_SERVERS_PATH\$wl_managed_server_name\data\ldap
}

# START THE NODEMANAGER NOW
cp $wl_tmp_files_dir\installMSconfigCommon.py $wl_tmp_files_dir\installMSstartNodeManager.py
Add-Content "$wl_tmp_files_dir\installMSstartNodeManager.py" "try:"
$temp_path=$WL_NODEMANAGER_HOME.Replace('\','/')
Add-Content "$wl_tmp_files_dir\installMSstartNodeManager.py" "    startNodeManager(verbose=`"true`", NodeManagerHome=`"$temp_path`", PropertiesFile=`"$temp_path/nodemanager.properties`")"
Add-Content "$wl_tmp_files_dir\installMSstartNodeManager.py" "    nodeManagerConnect()"
Add-Content "$wl_tmp_files_dir\installMSstartNodeManager.py" "    nmDisconnect()"
Add-Content "$wl_tmp_files_dir\installMSstartNodeManager.py" "except:"
Add-Content "$wl_tmp_files_dir\installMSstartNodeManager.py" "    apply(traceback.print_exception, sys.exc_info())"
Add-Content "$wl_tmp_files_dir\installMSstartNodeManager.py" "    dumpStack()"
Add-Content "$wl_tmp_files_dir\installMSstartNodeManager.py" "    exit(exitcode=-1)"
Add-Content "$wl_tmp_files_dir\installMSstartNodeManager.py" ""
Add-Content "$wl_tmp_files_dir\installMSstartNodeManager.py" "exit()"

echo "STARTING THE NODE MANAGER..."
$error.clear()
Start-MS "$WL_JAR" "$wl_tmp_files_dir\installMSstartNodeManager.py"
check_error "ERROR:WHILE STARTING NODE MANAGER"
echo "NODE MANAGER STARTED SUCCESSFULLY!"

# FINALLY NEED TO START THIS MANAGED SERVER
cp $wl_tmp_files_dir\installMSconfigCommon.py $wl_tmp_files_dir\installMSstartManagedServer.py
Add-Content "$wl_tmp_files_dir\installMSstartManagedServer.py" "try:"
# CONNECTING TO NODEMANAGER
$temp_path=$WL_DOMAIN_PATH.Replace('\','/')
Add-Content "$wl_tmp_files_dir\installMSstartManagedServer.py" "    nmConnect(`"$wl_node_manager_user`", `"$wl_node_manager_password`", `"$wl_node_manager_addr`", $wl_node_manager_port, `"$wl_domain`", `"$temp_path`", `"plain`", `"true`")"
# STARTING MANAGEDSERVER
Add-Content "$wl_tmp_files_dir\installMSstartManagedServer.py" "    nmStart(`"$wl_managed_server_name`",  `"$temp_path`")"
Add-Content "$wl_tmp_files_dir\installMSstartManagedServer.py" "    nmDisconnect()"
Add-Content "$wl_tmp_files_dir\installMSstartManagedServer.py" ""
#Test connections "
# TESTING NODEMANAGER CONNECTION
Add-Content "$wl_tmp_files_dir\installMSstartManagedServer.py" "    nodeManagerConnect()"
Add-Content "$wl_tmp_files_dir\installMSstartManagedServer.py" "    nmDisconnect()"
# TESTING ADMINSERVER CONNECTION
Add-Content "$wl_tmp_files_dir\installMSstartManagedServer.py" "    connect(`"$wl_admin_user`", `"$wl_admin_password`", adminServerURL)"
Add-Content "$wl_tmp_files_dir\installMSstartManagedServer.py" "    disconnect()"
Add-Content "$wl_tmp_files_dir\installMSstartManagedServer.py" ""
Add-Content "$wl_tmp_files_dir\installMSstartManagedServer.py" "except:"
Add-Content "$wl_tmp_files_dir\installMSstartManagedServer.py" "    apply(traceback.print_exception, sys.exc_info())"
Add-Content "$wl_tmp_files_dir\installMSstartManagedServer.py" "    dumpStack()"
Add-Content "$wl_tmp_files_dir\installMSstartManagedServer.py" "    exit(exitcode=-1)"
Add-Content "$wl_tmp_files_dir\installMSstartManagedServer.py" ""
Add-Content "$wl_tmp_files_dir\installMSstartManagedServer.py" "exit()"

echo "STARTING THE MANAGED SERVER..."
$error.clear()
Start-MS "$WL_JAR" "$wl_tmp_files_dir\installMSstartManagedServer.py"
check_error "ERROR:WHILE STARTING MANAGED SERVER"
echo "MANAGED SERVER STARTED SUCCESSFULLY!"
# SCRIPT EXECUTION -- END

supported-os-details.exec-script:
$error.clear()
# Auto convert input properties to local Powershell variables

# Parsing JSON elements into Powershell object
function ParseJsonProps ($jsonFile)
{
 $jsonjsonCharCount = 0
 $newObjStart = $false
 $arrayStart = $false
 $tokenStart = $false
 $valueStart = $false
 $escapeStart = $false
 
 $jsonFileLen = $jsonFile.length
 $jsonObj = New-Object System.Text.StringBuilder
 
 ForEach($c in $jsonFile.ToCharArray()) {
    $jsonCharCount++
    $tmpStr = ""
    switch -regex ($c) {
        "{" { 
            if($newObjStart -eq $false) {
                $tmpStr = "(New-Object PSObject "
                $newObjStart = $true
            } else {
                $tmpStr = $c
            }
        }
        "}" {
            if($jsonCharCount -lt $jsonFileLen) {
                $tmpStr = $c
            } else {
                $tmpStr = ")"
            }
        }
        '"' {
            if($tokenStart -eq $false -and $valueStart -eq $false -and $arrayStart -eq $false) {
                $tmpStr = '| Add-Member -Passthru NoteProperty "'
            } else {
                if($escapeStart -eq $false) {
                    $tmpStr = $c
                } else {
                    $tmpStr = "```""
                    $escapeStart = $false
                }
            }
            $tokenStart = $true
        }
        ":" {
            if($valueStart -eq $true -or $arrayStart -eq $true) {
                $tmpStr = $c
            } else {
                $tmpStr = " "
            }
            $valueStart = $true
        }
        "," {
            if($arrayStart) {
                $tmpStr = $c
            } else {
                $valueStart = $false
                $tokenStart = $false
            }
        }	
        "\[" {
            $tmpStr = "@("
            $arrayStart = $true
        }
        "\]" {
            $tmpStr = ")"
            $arrayStart = $false
        }
        "``" {
            $tmpStr = "````"
        }
        "\\" {
            if($escapeStart -eq $false) {
                $escapeStart = $true
            } else {
                $tmpStr = $c
                $escapeStart = $false
            }
        }
        "\$" {
            if($escapeStart -eq $false) {
                $tmpStr = '`$'
            } else {
                $tmpStr = '`$'
                $escapeStart = $false
            }
        }
        "n" {
            if($escapeStart -eq $false) {
                $tmpStr = $c
            } else {
                $tmpStr = "``n"
                $escapeStart = $false
            }
        }
        "r" {
            if($escapeStart -eq $false) {
                $tmpStr = $c
            } else {
                $tmpStr = "``r"
                $escapeStart = $false
            }
        }
        "t" {
            if($escapeStart -eq $false) {
                $tmpStr = $c
            } else {
                $tmpStr = "``t"
                $escapeStart = $false
            }
        }
        "b" {
            if($escapeStart -eq $false) {
                $tmpStr = $c
            } else {
                $tmpStr = "``b"
                $escapeStart = $false
            }
        }
        default {
            if($escapeStart -eq $false) {
                $tmpStr = $c
            } else {
                $tmpStr = "\$c"
                $escapeStart = $false
            }
            #$tmpStr = $c
            #if($escapeStart -eq $true) {
            #    $escapeStart = $false
            #}
        }
    }                        
    [void] $jsonObj.Append($tmpStr)
 }
 
 $jsonObj | Invoke-Expression
}

$vmw_appd_json_file = Get-Content "$($Args[1])"
$vmw_appd_json_obj = ParseJsonProps $vmw_appd_json_file
$vmw_appd_json_props = $vmw_appd_json_obj.PsObject.Properties

# Assigning obj to local variable
ForEach ($vmw_appd_obj_prop in $vmw_appd_json_props)
{
 $vmw_appd_obj_prop_name = $vmw_appd_obj_prop.Name;
 #$vmw_appd_obj_prop_name_full = '$vmw_appd_json_obj' + '.' + $vmw_appd_obj_prop_name
 #$vmw_appd_obj_prop_value = Invoke-Expression $vmw_appd_obj_prop_name_full
 Set-Variable -Name $vmw_appd_obj_prop_name -value $vmw_appd_json_obj."$vmw_appd_obj_prop_name"
}


# Execute user's Powershell script
$error.clear()
$global:LASTEXITCODE = $null
. "$($Args[0])" 2>&1 | out-file -encoding Default "$($Args[3])"

if ($LASTEXITCODE -and $LASTEXITCODE -ne 0) {
 "`nABORT. LASTEXITCODE is non-zero.`n" | out-file -encoding Default -append $($Args[3])
 exit $LASTEXITCODE
}

if ($error.count -gt 0) {
 "`nABORT. Encountered error in Powershell.`n" | out-file -encoding Default -append $($Args[3])
 exit $error.count
 }

# Export Powershell variables to output properties file
ForEach ($vmw_appd_obj_prop in $vmw_appd_json_props)
{
 $vmw_appd_obj_prop_name = $vmw_appd_obj_prop.Name;
 $vmw_appd_obj_prop_type = (($vmw_appd_json_obj.($vmw_appd_obj_prop_name)).GetType()).Name

 # Export only String AppD's properties; Output Array properties is not currently supported.
 if ($vmw_appd_obj_prop_type.Contains("String")) {
      $vmw_appd_obj_prop_name_expanded = '$' + $vmw_appd_obj_prop_name

      # The ExpandString takes care of three cases. 
      # Case-I when the string is set via input properties
      # Case-II when the string is set via input properties and overridden in script (install/configure..)
      # Case-III lastly when it is only set via script (install/configure..) and left empty in input props
      #
      # Also note that powershell as is does not care if this variable was a computed 
      # or a string variable that filter happens at a different layer.
      #
      $expandedUserVariable = $ExecutionContext.InvokeCommand.ExpandString($vmw_appd_obj_prop_name_expanded)

     ($vmw_appd_obj_prop_name + '=' + $expandedUserVariable) | Add-Content $($Args[2]) 
 }
}
